import React, { useState } from "react";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";

// Opções e legendas por extenso
const veiasProfundas = [
  "Veia Femoral Comum",
  "Veia Femoral Superficial",
  "Veia Femoral Profunda",
  "Veia Poplítea",
  "Veias Tibiais posteriores",
  "Veias Tibiais anteriores",
  "Veias Gastrocnêmicas",
  "Veias Soleares",
];
const veiasSuperficiais = [
  "JSF",
  "Safena Magna",
  "JSP",
  "Safena Parva",
];
const profOptions = [
  "pérvia e competente",
  "pérvia e incompetente",
  "não compressível e sem fluxo (sugestivo de trombose)",
  "semi compressível, sugestivo de recanalização parcial",
];
const supOptions = [
  "pérvia e competente",
  "pérvia e incompetente",
  "não compressível e sem fluxo (trombose)",
  "ausente",
];
const lados = ["Direito", "Esquerdo", "Ambos"];
const legendaCampos = {
  "JSF": "JSF",
  "JSP": "JSP",
  "cm_acima_joelho": "cm acima do joelho",
  "cm_abaixo_joelho": "cm abaixo do joelho",
  "cm_acima_tornozelo": "cm acima do tornozelo",
  "tornozelo": "tornozelo",
};

const perfurantesStatusOptions = [
  "pérvia e competente",
  "pérvia e incompetente",
];
const perfurantesSegmentoOptions = [
  "cm acima do joelho",
  "cm abaixo do joelho",
  "cm acima do tornozelo",
];

// Bloco de campos por lado
function BlocoCampos({ lado, profundas, superficiais, magna, parva, perfurantes, onProfundas, onSuperficiais, onMagna, onParva, onPerfurantes, jsfDiametro, setJsfDiametro, jspDiametro, setJspDiametro, observacao, onObservacao }) {
  return (
    <div style={{marginBottom: 24, width: '100%', maxWidth: 1100, minWidth: 320, padding: '24px 32px', boxSizing: 'border-box', background: 'rgba(0,0,0,0.10)', borderRadius: 18, boxShadow: '0 2px 16px 0 #0002', marginLeft: 'auto', marginRight: 'auto', display: 'flex', flexDirection: 'column', gap: 18}}>
      <div style={{marginTop:14,marginBottom:8,fontWeight:700}}>
        Sistema Venoso Profundo ({lado}):
      </div>
      {veiasProfundas.map(veia => (
        <div key={veia} style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6, flexWrap: 'wrap' }}>
          <label style={{ minWidth: 200 }}>{veia}:</label>
          <select value={profundas[veia]} onChange={e=>onProfundas({...profundas, [veia]:e.target.value})} style={{flex: 1, minWidth: 180, maxWidth: 500, padding:6, borderRadius:6}}>
            {profOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
        </div>
      ))}
      <div style={{marginTop:20,fontWeight:700}}>
        Sistema Venoso Superficial ({lado}):
      </div>
      {veiasSuperficiais.map(veia => (
        <div key={veia} style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6, flexWrap: 'wrap', width: '100%' }}>
          <label style={{ minWidth: 200 }}>{veia}:</label>
          <select
            value={superficiais[veia]}
            onChange={e=>onSuperficiais({...superficiais, [veia]:e.target.value})}
            style={{flex: 1, minWidth: 180, maxWidth: 500, padding:6, borderRadius:6}}
          >
            {supOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
          {(veia === "JSF") && (
            <input type="number" min={0} step={0.1} placeholder="Diâmetro (mm)" value={jsfDiametro[lado] || ""} onChange={e => setJsfDiametro(prev => ({ ...prev, [lado]: e.target.value }))} style={{ width: 120, marginLeft: 8, borderRadius: 5, border: "1px solid #0eb8d0", padding: 6 }} />
          )}
          {(veia === "JSP") && (
            <input type="number" min={0} step={0.1} placeholder="Diâmetro (mm)" value={jspDiametro[lado] || ""} onChange={e => setJspDiametro(prev => ({ ...prev, [lado]: e.target.value }))} style={{ width: 120, marginLeft: 8, borderRadius: 5, border: "1px solid #0eb8d0", padding: 6 }} />
          )}
          {veia === "Safena Magna" && (
            <div style={{ width: '100%' }}>
              <SafenaMagnaExtra status={superficiais[veia]} valores={magna} onChange={onMagna} />
            </div>
          )}
          {veia === "Safena Parva" && (
            <div style={{ width: '100%' }}>
              <SafenaParvaExtra status={superficiais[veia]} valores={parva} onChange={onParva} />
            </div>
          )}
        </div>
      ))}
      {/* Bloco de veias perfurantes */}
      <div style={{marginTop:20, fontWeight:700}}>Veias Perfurantes ({lado}):</div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6, flexWrap: 'wrap' }}>
        <label style={{ minWidth: 200 }}>Veias Perfurantes:</label>
        <select
          value={perfurantes.status}
          onChange={e => onPerfurantes({ ...perfurantes, status: e.target.value })}
          style={{flex: 1, minWidth: 180, maxWidth: 500, padding:6, borderRadius:6}}
        >
          {perfurantesStatusOptions.map(opt => <option key={opt}>{opt}</option>)}
        </select>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6, flexWrap: 'wrap', marginLeft: 12 }}>
        <select
          value={perfurantes.segmento}
          onChange={e => onPerfurantes({ ...perfurantes, segmento: e.target.value, valor: "" })}
          style={{ minWidth: 180, maxWidth: 260, padding:6, borderRadius:6 }}
        >
          <option value="">Selecione o segmento</option>
          {perfurantesSegmentoOptions.map(opt => <option key={opt}>{opt}</option>)}
        </select>
        {perfurantes.segmento && (
          <input
            type="number"
            min={0}
            step={0.1}
            value={perfurantes.valor}
            onChange={e => onPerfurantes({ ...perfurantes, valor: e.target.value })}
            placeholder="cm"
            style={{ width: 80, marginLeft: 6, borderRadius: 6, padding: 6, border: '1.5px solid #0eb8d0' }}
          />
        )}
      </div>
      {/* Caixa de Observação por membro */}
      <div style={{ width: '100%', marginTop: 12 }}>
        <label style={{ fontWeight: 600, fontSize: 16, marginBottom: 6, display: 'block' }}>Observações ({lado}):</label>
        <textarea
          value={observacao || ''}
          onChange={e => onObservacao(e.target.value)}
          placeholder={`Digite observações adicionais do exame do membro ${lado.toLowerCase()}...`}
          style={{ width: '100%', minHeight: 60, fontSize: 15, borderRadius: 8, border: '1.5px solid #0eb8d0', padding: 10, resize: 'vertical', marginTop: 4 }}
        />
      </div>
    </div>
  );
}

// Componentes extras das safenas
function SafenaMagnaExtra({ status, valores, onChange }) {
  if (status !== "pérvia e incompetente") return null;
  return (
    <div style={{ width: '100%', margin: '4px 0 8px 0', padding: 0, display: 'flex', flexDirection: 'column', alignItems: 'flex-start', background: 'none' }}>
      <div style={{ display: 'flex', flexDirection: 'row', gap: 10, alignItems: 'center', width: '100%', marginBottom: 4, flexWrap: 'wrap', justifyContent: 'flex-start' }}>
        <label style={{ minWidth: 90, fontWeight: 500 }}>Coxa:
          <input type="number" min={0} step={0.1} value={valores.coxa} onChange={e => onChange({ ...valores, coxa: e.target.value })} style={{ width: 60, marginLeft: 4 }} />
        </label>
        <label style={{ minWidth: 90, fontWeight: 500 }}>Perna:
          <input type="number" min={0} step={0.1} value={valores.perna} onChange={e => onChange({ ...valores, perna: e.target.value })} style={{ width: 60, marginLeft: 4 }} />
        </label>
        <label style={{ minWidth: 110, fontWeight: 500 }}>Tornozelo:
          <input type="number" min={0} step={0.1} value={valores.tornozelo} onChange={e => onChange({ ...valores, tornozelo: e.target.value })} style={{ width: 60, marginLeft: 4 }} />
        </label>
      </div>
      <div style={{ display: 'flex', flexDirection: 'row', gap: 10, alignItems: 'center', width: '100%', flexWrap: 'wrap', justifyContent: 'flex-start' }}>
        <label style={{ minWidth: 120, fontWeight: 500 }}>Início:
          <select value={valores.inicio} onChange={e => onChange({ ...valores, inicio: e.target.value, inicio_valor: "" })} style={{ marginLeft: 4, width: 140 }}>
