import React, { useState } from "react";

export default function MMIIVenoso() {
  const [form, setForm] = useState({
    nome: "",
    idade: "",
    data: "",
    lado: "direito"
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Aqui você pode adicionar lógica para salvar os dados
    alert("Exame cadastrado!\n" + JSON.stringify(form, null, 2));
  }

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(120deg,#101824 0%,#1c2740 100%)",
      color: "#fff",
      fontFamily: "Segoe UI, Inter, Arial, sans-serif",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      padding: 0
    }}>
      <img
        src="/venoai-logo.png"
        alt="VENO.AI"
        style={{
          width: 140, // reduced from 320
          marginTop: 16, // less top margin
          marginBottom: 10, // less bottom margin
          filter: "drop-shadow(0 10px 32px #00e0ff90)",
          animation: "logoGlow 3s ease-in-out infinite alternate"
        }}
      />
      <div style={{
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        marginBottom: 0,
        marginTop: 0,
        position: "absolute", // stick to top
        top: 0,
        left: 0,
        zIndex: 2
      }}>
        <form onSubmit={handleSubmit} style={{
          background: "#18243a",
          borderRadius: 14,
          boxShadow: "0 4px 24px #00e0ff20",
          padding: "18px 24px 14px 24px",
          display: "flex",
          flexDirection: "row",
          alignItems: "center",
          gap: 18,
          maxWidth: 900,
          width: "auto",
          minWidth: 0
        }}>
          <input
            type="text"
            name="nome"
            placeholder="Nome do Paciente"
            value={form.nome}
            onChange={handleChange}
            required
            style={inputStyle}
            autoComplete="off"
          />
          <input
            type="number"
            name="idade"
            placeholder="Idade"
            value={form.idade}
            onChange={handleChange}
            min={0}
            max={120}
            required
            style={{ ...inputStyle, width: 90 }}
          />
          <input
            type="date"
            name="data"
            placeholder="Data do Exame"
            value={form.data}
            onChange={handleChange}
            required
            style={{ ...inputStyle, width: 160 }}
          />
          <select
            name="lado"
            value={form.lado}
            onChange={handleChange}
            style={{ ...inputStyle, width: 120, color: form.lado ? "#222" : "#888" }}
            required
          >
            <option value="direito">Direito</option>
            <option value="esquerdo">Esquerdo</option>
            <option value="ambos">Ambos</option>
          </select>
          <button
            type="submit"
            style={buttonStyle}
          >
            Cadastrar Exame
          </button>
        </form>
      </div>
      <style>{`
        @keyframes logoGlow {
          0% {
            filter: drop-shadow(0 10px 32px #00e0ff90);
          }
          100% {
            filter: drop-shadow(0 18px 48px #00e0ffc0);
          }
        }
      `}</style>
    </div>
  );
}

const inputStyle = {
  background: "#f7fbff",
  border: "1.5px solid #0eb8d0",
  borderRadius: 7,
  padding: "8px 12px",
  fontSize: 15,
  color: "#222",
  outline: "none",
  fontFamily: "inherit",
  fontWeight: 500,
  width: 180,
  minWidth: 0
};

const buttonStyle = {
  background: "#0eb8d0",
  color: "#fff",
  fontWeight: 600,
  fontSize: 16,
  padding: "10px 22px",
  border: "none",
  borderRadius: 8,
  cursor: "pointer",
  letterSpacing: 0.5,
  boxShadow: "0 2px 10px #00e0ff30",
  transition: ".2s",
  marginLeft: 8,
  minWidth: 140
};
