import React, { useState } from "react";

// Listas de veias e opções
const veiasProfundas = [
  "Veia Femoral Comum",
  "Veia Femoral Superficial",
  "Veia Femoral Profunda",
  "Veia Poplítea",
  "Veias Tibiais posteriores",
  "Veias Tibiais anteriores",
  "Veias Gastrocnêmicas",
  "Veias Soleares",
];

const veiasSuperficiais = [
  "JSF",
  "Safena Magna",
  "JSP",
  "Safena Parva",
];

const profOptions = [
  "pérvia e competente",
  "pérvia e incompetente",
  "não compressível e sem fluxo (sugestivo de trombose)",
  "semi compressível, sugestivo de recanalização parcial",
];

const supOptions = [
  "pérvia e competente",
  "pérvia e insuficiente",
  "não compressível e sem fluxo (trombose)",
  "ausente"
];

const lados = ["Direito", "Esquerdo", "Ambos"];

// Novo componente para bloco de campos de um lado
function BlocoCampos({ lado, profundas, superficiais, magna, parva, onProfundas, onSuperficiais, onMagna, onParva }) {
  return (
    <div style={{marginBottom: 24}}>
      <div style={{marginTop:14,marginBottom:8,fontWeight:700}}>
        Sistema Venoso Profundo ({lado}):
      </div>
      {veiasProfundas.map(veia => (
        <div key={veia}>
          <label>{veia}:</label>
          <select value={profundas[veia]} onChange={e=>onProfundas({...profundas, [veia]:e.target.value})} style={{marginLeft:10,width:360}}>
            {profOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
        </div>
      ))}
      <div style={{marginTop:20,fontWeight:700}}>
        Sistema Venoso Superficial ({lado}):
      </div>
      {veiasSuperficiais.map(veia => (
        <div key={veia}>
          <label>{veia}:</label>
          <select
            value={superficiais[veia]}
            onChange={e=>onSuperficiais({...superficiais, [veia]:e.target.value})}
            style={{marginLeft:10,width:300}}
          >
            {supOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
          {veia==="Safena Magna" &&
            <SafenaMagnaExtra status={superficiais[veia]} valores={magna} onChange={onMagna} />
          }
          {veia==="Safena Parva" &&
            <SafenaParvaExtra status={superficiais[veia]} valores={parva} onChange={onParva} />
          }
        </div>
      ))}
    </div>
  );
}

// Componente extra para Safena Magna insuficiente
function SafenaMagnaExtra({ status, valores, onChange }) {
  if (status !== "pérvia e insuficiente") return null;
  return (
    <div style={{ marginTop: 8, marginBottom: 8, padding: 8, background: "#223355", borderRadius: 6 }}>
      <div style={{ fontWeight: 600, color: "#0eb8d0", marginBottom: 4 }}>Campos extras para Safena Magna insuficiente</div>
      <div style={{ display: 'flex', gap: 12, marginBottom: 8 }}>
        <label>Diâmetro coxa (mm):
          <input type="number" min={0} step={0.1} value={valores.coxa} onChange={e => onChange({ ...valores, coxa: e.target.value })} style={{ width: 70, marginLeft: 6 }} />
        </label>
        <label>Diâmetro perna (mm):
          <input type="number" min={0} step={0.1} value={valores.perna} onChange={e => onChange({ ...valores, perna: e.target.value })} style={{ width: 70, marginLeft: 6 }} />
        </label>
        <label>Diâmetro tornozelo (mm):
          <input type="number" min={0} step={0.1} value={valores.tornozelo} onChange={e => onChange({ ...valores, tornozelo: e.target.value })} style={{ width: 70, marginLeft: 6 }} />
        </label>
      </div>
      <div style={{ display: 'flex', gap: 18, marginBottom: 8 }}>
        <label>Início da insuficiência:
          <select value={valores.inicio} onChange={e => onChange({ ...valores, inicio: e.target.value, inicio_valor: "" })} style={{ marginLeft: 6 }}>
            <option value="">Selecione</option>
            <option value="JSF">JSF</option>
            <option value="cm_acima_joelho">cm acima do joelho</option>
            <option value="cm_abaixo_joelho">cm abaixo do joelho</option>
            <option value="cm_acima_tornozelo">cm acima do tornozelo</option>
          </select>
          {valores.inicio && valores.inicio.startsWith("cm_") && (
            <input type="number" min={0} step={0.1} value={valores.inicio_valor} onChange={e => onChange({ ...valores, inicio_valor: e.target.value })} placeholder="cm" style={{ width: 60, marginLeft: 6 }} />
          )}
        </label>
        <label>Término da insuficiência:
          <select value={valores.fim} onChange={e => onChange({ ...valores, fim: e.target.value, fim_valor: "" })} style={{ marginLeft: 6 }}>
            <option value="">Selecione</option>
            <option value="cm_acima_joelho">cm acima do joelho</option>
            <option value="cm_abaixo_joelho">cm abaixo do joelho</option>
            <option value="cm_acima_tornozelo">cm acima do tornozelo</option>
            <option value="tornozelo">tornozelo</option>
          </select>
          {valores.fim && valores.fim.startsWith("cm_") && (
            <input type="number" min={0} step={0.1} value={valores.fim_valor} onChange={e => onChange({ ...valores, fim_valor: e.target.value })} placeholder="cm" style={{ width: 60, marginLeft: 6 }} />
          )}
        </label>
      </div>
    </div>
  );
}

// Componente extra para Safena Parva insuficiente
function SafenaParvaExtra({ status, valores, onChange }) {
  if (status !== "pérvia e insuficiente") return null;
  return (
    <div style={{ marginTop: 8, marginBottom: 8, padding: 8, background: "#223355", borderRadius: 6 }}>
      <div style={{ fontWeight: 600, color: "#0eb8d0", marginBottom: 4 }}>Campos extras para Safena Parva insuficiente</div>
      <div style={{ display: 'flex', gap: 12, marginBottom: 8 }}>
        <label>Diâmetro proximal (mm):
          <input type="number" min={0} step={0.1} value={valores.proximal} onChange={e => onChange({ ...valores, proximal: e.target.value })} style={{ width: 70, marginLeft: 6 }} />
        </label>
        <label>Diâmetro distal (mm):
          <input type="number" min={0} step={0.1} value={valores.distal} onChange={e => onChange({ ...valores, distal: e.target.value })} style={{ width: 70, marginLeft: 6 }} />
        </label>
      </div>
      <div style={{ display: 'flex', gap: 18, marginBottom: 8 }}>
        <label>Início da insuficiência:
          <select value={valores.inicio} onChange={e => onChange({ ...valores, inicio: e.target.value, inicio_valor: "" })} style={{ marginLeft: 6 }}>
            <option value="">Selecione</option>
            <option value="JSP">JSP</option>
            <option value="cm_abaixo_joelho">cm abaixo do joelho</option>
          </select>
          {valores.inicio === "cm_abaixo_joelho" && (
            <input type="number" min={0} step={0.1} value={valores.inicio_valor} onChange={e => onChange({ ...valores, inicio_valor: e.target.value })} placeholder="cm" style={{ width: 60, marginLeft: 6 }} />
          )}
        </label>
        <label>Término da insuficiência:
          <select value={valores.fim} onChange={e => onChange({ ...valores, fim: e.target.value, fim_valor: "" })} style={{ marginLeft: 6 }}>
            <option value="">Selecione</option>
            <option value="cm_acima_tornozelo">cm acima do tornozelo</option>
            <option value="tornozelo">tornozelo</option>
          </select>
          {valores.fim === "cm_acima_tornozelo" && (
            <input type="number" min={0} step={0.1} value={valores.fim_valor} onChange={e => onChange({ ...valores, fim_valor: e.target.value })} placeholder="cm" style={{ width: 60, marginLeft: 6 }} />
          )}
        </label>
      </div>
    </div>
  );
}

function MMIIVenoso() {
  const [nome, setNome] = useState("");
  const [idade, setIdade] = useState("");
  const [data, setData] = useState("");
  const [lado, setLado] = useState("");
  // Estados aninhados por lado
  const ladosIndividuais = ["Direito", "Esquerdo"];
  const [profundas, setProfundas] = useState({
    Direito: Object.fromEntries(veiasProfundas.map(v=>[v, profOptions[0]])),
    Esquerdo: Object.fromEntries(veiasProfundas.map(v=>[v, profOptions[0]])),
  });
  const [superficiais, setSuperficiais] = useState({
    Direito: Object.fromEntries(veiasSuperficiais.map(v=>[v, supOptions[0]])),
    Esquerdo: Object.fromEntries(veiasSuperficiais.map(v=>[v, supOptions[0]])),
  });
  const [magna, setMagna] = useState({
    Direito: {coxa:"",perna:"",tornozelo:"",inicio:"",inicio_valor:"",fim:"",fim_valor:""},
    Esquerdo: {coxa:"",perna:"",tornozelo:"",inicio:"",inicio_valor:"",fim:"",fim_valor:""},
  });
  const [parva, setParva] = useState({
    Direito: {proximal:"",distal:"",inicio:"",inicio_valor:"",fim:"",fim_valor:""},
    Esquerdo: {proximal:"",distal:"",inicio:"",inicio_valor:"",fim:"",fim_valor:""},
  });

  // Remover uso de 'form' e usar estados individuais
  function handleChange(e) {
    const { name, value } = e.target;
    if (name === "nome") setNome(value);
    else if (name === "idade") setIdade(value);
    else if (name === "data") setData(value);
    else if (name === "lado") setLado(value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Aqui você pode adicionar lógica para salvar os dados
    alert("Exame cadastrado!\n" + JSON.stringify({ nome, idade, data, lado }, null, 2));
  }

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(120deg,#101824 0%,#1c2740 100%)",
      color: "#fff",
      fontFamily: "Segoe UI, Inter, Arial, sans-serif",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      padding: 0
    }}>
      <img
        src="/venoai-logo.png"
        alt="VENO.AI"
        style={{
          width: 140, // reduced from 320
          marginTop: 16, // less top margin
          marginBottom: 10, // less bottom margin
          filter: "drop-shadow(0 10px 32px #00e0ff90)",
          animation: "logoGlow 3s ease-in-out infinite alternate"
        }}
      />
      <div style={{
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        marginBottom: 0,
        marginTop: 0
      }}>
        <form onSubmit={handleSubmit} style={{
          background: "#18243a",
          borderRadius: 14,
          boxShadow: "0 4px 24px #00e0ff20",
          padding: "14px 18px 10px 18px",
          display: "flex",
          flexDirection: "row",
          alignItems: "center",
          gap: 10,
          width: "100%",
          maxWidth: 900,
          minWidth: 0,
          justifyContent: "center"
        }}>
          <input
            type="text"
            name="nome"
            placeholder="Nome do Paciente"
            value={nome}
            onChange={handleChange}
            required
            style={{ ...inputStyle, width: 220 }}
            autoComplete="off"
          />
          <input
            type="number"
            name="idade"
            placeholder="Idade"
            value={idade}
            onChange={handleChange}
            min={0}
            max={120}
            required
            style={{ ...inputStyle, width: 90 }}
          />
          <input
            type="date"
            name="data"
            placeholder="Data do Exame"
            value={data}
            onChange={handleChange}
            required
            style={{ ...inputStyle, width: 160 }}
          />
          <select
            name="lado"
            value={lado}
            onChange={handleChange}
            style={{ ...inputStyle, width: 120, color: lado ? "#222" : "#888" }}
            required
          >
            <option value="">Selecione</option>
            <option value="direito">Direito</option>
            <option value="esquerdo">Esquerdo</option>
            <option value="ambos">Ambos</option>
          </select>
          <button
            type="submit"
            style={buttonStyle}
          >
            Cadastrar Exame
          </button>
        </form>
      </div>
      {/* Renderização condicional dos blocos de campos */}
      <div style={{ width: "100%", maxWidth: 900, marginTop: 24, display: lado === "ambos" ? "flex" : "block", gap: 32 }}>
        {lado === "direito" && (
          <BlocoCampos
            lado="Direito"
            profundas={profundas.Direito}
            superficiais={superficiais.Direito}
            magna={magna.Direito}
            parva={parva.Direito}
            onProfundas={val => setProfundas(prev => ({ ...prev, Direito: val }))}
            onSuperficiais={val => setSuperficiais(prev => ({ ...prev, Direito: val }))}
            onMagna={val => setMagna(prev => ({ ...prev, Direito: val }))}
            onParva={val => setParva(prev => ({ ...prev, Direito: val }))}
          />
        )}
        {lado === "esquerdo" && (
          <BlocoCampos
            lado="Esquerdo"
            profundas={profundas.Esquerdo}
            superficiais={superficiais.Esquerdo}
            magna={magna.Esquerdo}
            parva={parva.Esquerdo}
            onProfundas={val => setProfundas(prev => ({ ...prev, Esquerdo: val }))}
            onSuperficiais={val => setSuperficiais(prev => ({ ...prev, Esquerdo: val }))}
            onMagna={val => setMagna(prev => ({ ...prev, Esquerdo: val }))}
            onParva={val => setParva(prev => ({ ...prev, Esquerdo: val }))}
          />
        )}
        {lado === "ambos" && (
          <>
            <BlocoCampos
              lado="Direito"
              profundas={profundas.Direito}
              superficiais={superficiais.Direito}
