                <div>
                  <label style={{
                    display: "block",
                    fontSize: 14,
                    fontWeight: 500,
                    color: "#abfaff",
                    marginBottom: 8
                  }}>
                    Lado
                  </label>
                  <select
                    value={lado}
                    onChange={(e) => handleLadoChange(e.target.value)}
                    style={{
                      width: "100%",
                      padding: "12px 16px",
                      border: "1px solid #0eb8d0",
                      borderRadius: 8,
                      background: "#1c2740",
                      color: "#fff",
                      fontSize: 14,
                      outline: "none"
                    }}
                  >
                    <option value="">Selecione o lado</option>
                    {ladosPossiveis.map((l) => (
                      <option key={l} value={l} style={{ background: "#1c2740" }}>
                        {l}
                      </option>
                    ))}
                  </select>
                </div>

                <div style={{ display: "flex", gap: 12 }}>
                  <button
                    onClick={gerarLaudo}
                    style={{
                      flex: 1,
                      background: "#28a745",
                      border: "none",
                      borderRadius: 10,
                      padding: "12px 20px",
                      color: "#fff",
                      fontWeight: 600,
                      fontSize: 15,
                      cursor: "pointer",
                      transition: ".2s",
                      letterSpacing: 0.5
                    }}
                    onMouseEnter={(e) => {
                      e.target.style.background = "#218838";
                      e.target.style.transform = "translateY(-2px)";
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.background = "#28a745";
                      e.target.style.transform = "translateY(0)";
                    }}
                  >
                    Gerar Laudo
                  </button>
                  <button
                    onClick={limparFormulario}
                    style={{
                      background: "#6c757d",
                      border: "none",
                      borderRadius: 10,
                      padding: "12px 20px",
                      color: "#fff",
                      fontWeight: 600,
                      fontSize: 15,
                      cursor: "pointer",
                      transition: ".2s",
                      letterSpacing: 0.5
                    }}
                    onMouseEnter={(e) => {
                      e.target.style.background = "#5a6268";
                      e.target.style.transform = "translateY(-2px)";
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.background = "#6c757d";
                      e.target.style.transform = "translateY(0)";
                    }}
                  >
                    Limpar
                  </button>
                </div>

                {exameEmEdicao && (
                  <div style={{ display: "flex", gap: 12 }}>
                    <button
                      onClick={atualizarExame}
                      style={{
                        flex: 1,
                        background: "#0eb8d0",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 20px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 15,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#0ca8b8";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#0eb8d0";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Atualizar Exame
                    </button>
                    <button
                      onClick={() => setExameEmEdicao(null)}
                      style={{
                        background: "#6c757d",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 20px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 15,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#5a6268";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#6c757d";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Cancelar
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Laudo Gerado */}
            <div style={{
              background: "#121e38",
              borderRadius: 15,
              padding: 24,
              border: "1px solid #0eb8d0"
            }}>
              <div style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 20
              }}>
                <h2 style={{
                  fontSize: 24,
                  fontWeight: 600,
                  color: "#fff",
                  margin: 0
                }}>Laudo Gerado</h2>
                {laudo && (
                  <div style={{ display: "flex", gap: 8 }}>
                    <button
                      onClick={() => {
                        const laudos = gerarLaudo();
                        if (laudos) {
                          const lados = Object.keys(laudos);
                          gerarPDFExame({ nome, data, lados, laudosPorLado: laudos });
                        }
                      }}
                      style={{
                        background: "#dc3545",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 16px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 14,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5,
                        display: "flex",
                        alignItems: "center",
                        gap: 8
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#c82333";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#dc3545";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      <FiPrinter size={16} />
                      Gerar PDF
                    </button>
                    <button
                      onClick={salvarExameAtual}
                      style={{
                        background: "#0eb8d0",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 16px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 14,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#0ca8b8";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#0eb8d0";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Salvar
                    </button>
                  </div>
                )}
              </div>

              {laudo ? (
                <div style={{
                  background: "#1c2740",
                  padding: 16,
                  borderRadius: 8,
                  border: "1px solid #0eb8d0",
                  maxHeight: "400px",
                  overflowY: "auto"
                }}>
                  <pre style={{
                    whiteSpace: "pre-wrap",
                    fontSize: 13,
                    fontFamily: "monospace",
                    color: "#fff",
                    margin: 0,
                    lineHeight: 1.5
                  }}>{laudo}</pre>
                </div>
              ) : (
                <div style={{
                  color: "#abfaff",
                  textAlign: "center",
                  padding: "40px 20px",
                  fontSize: 16
                }}>
                  Gere um laudo para visualizar aqui
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Modal de Visualização */}
      {exameVisualizando && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: "rgba(0, 0, 0, 0.8)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: 20,
          zIndex: 1000
        }}>
          <div style={{
            background: "#121e38",
            borderRadius: 15,
            maxWidth: 800,
            width: "100%",
            maxHeight: "80vh",
            overflow: "hidden",
            border: "1px solid #0eb8d0"
          }}>
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              padding: 24,
              borderBottom: "1px solid #0eb8d0"
            }}>
              <h3 style={{
                fontSize: 20,
                fontWeight: 600,
                color: "#fff",
                margin: 0
              }}>
                {exameVisualizando.nome} - {exameVisualizando.data}
              </h3>
              <button
                onClick={() => setExameVisualizando(null)}
                style={{
                  background: "#dc3545",
                  border: "none",
                  borderRadius: 8,
                  padding: 8,
                  color: "#fff",
                  cursor: "pointer",
                  transition: ".2s"
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = "#c82333";
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = "#dc3545";
                }}
              >
                <FiX size={20} />
              </button>
            </div>
            <div style={{
              padding: 24,
              overflowY: "auto",
              maxHeight: "60vh"
            }}>
              <pre style={{
                whiteSpace: "pre-wrap",
                fontSize: 13,
                fontFamily: "monospace",
                color: "#fff",
                margin: 0,
                lineHeight: 1.5
              }}>{exameVisualizando.laudo}</pre>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
