    if (!laudo) return <div style={{ color: "#888", fontSize: 17 }}>Preencha os campos e clique em "Visualizar Laudo"</div>;
    return laudo.split("\n").map((l,i) => {
      if (l.startsWith("PACIENTE:") || l.startsWith("DOPPLER VENOSO") || l.startsWith("Sistema Venoso Profundo") || l.startsWith("Sistema Venoso Superficial") || l.startsWith("CONCLUSÃO")) {
        return <div key={i} style={{ fontWeight: "bold", color: "#0eb8d0", fontSize: 18 }}>{l}</div>;
      }
      if (l.startsWith("DATA:")) {
        return <div key={i} style={{ fontWeight: "600", color: "#aaffee" }}>{l}</div>;
      }
      return <div key={i} style={{ whiteSpace: "pre-wrap" }}>{l}</div>;
    });
  }

