                        value={camposD.superficial[veia]}
                        onChange={e => {
                          const novos = { ...camposD };
                          novos.superficial[veia] = e.target.value;
                          atualizaCampos("Direito", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      >
                        <option value="pérvia e competente">Pérvia e competente</option>
                        <option value="pérvia e insuficiente">Pérvia e insuficiente</option>
                        <option value="não compressível e sem fluxo (trombose)">Não compressível e sem fluxo (trombose)</option>
                      </select>
                    </div>

                    {/* Campos específicos para Safena Magna quando insuficiente */}
                    {veia === "Safena Magna" && camposD.superficial[veia] === "pérvia e insuficiente" && (
                      <div style={{ marginLeft: 20, marginBottom: 15, padding: 15, background: "#1a2332", borderRadius: 8 }}>
                        <h5 style={{ color: "#11b581", marginBottom: 10 }}>Dados da Safena Magna</h5>

                        {/* Diâmetros */}
                        <div style={{ marginBottom: 15 }}>
                          <h6 style={{ color: "#0eb8d0", marginBottom: 8 }}>Diâmetros (mm):</h6>
                          <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 10 }}>
                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Coxa:</label>
                              <input
                                type="number"
                                placeholder="mm"
                                value={camposD.safena["Safena Magna"].coxa}
                                onChange={e => {
                                  const novos = { ...camposD };
                                  novos.safena["Safena Magna"].coxa = e.target.value;
                                  atualizaCampos("Direito", novos);
                                }}
                                style={{ width: "100%", padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                              />
                            </div>
                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Perna:</label>
                              <input
                                type="number"
                                placeholder="mm"
                                value={camposD.safena["Safena Magna"].perna}
                                onChange={e => {
                                  const novos = { ...camposD };
                                  novos.safena["Safena Magna"].perna = e.target.value;
                                  atualizaCampos("Direito", novos);
                                }}
                                style={{ width: "100%", padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                              />
                            </div>
                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Tornozelo:</label>
                              <input
                                type="number"
                                placeholder="mm"
                                value={camposD.safena["Safena Magna"].tornozelo}
                                onChange={e => {
                                  const novos = { ...camposD };
                                  novos.safena["Safena Magna"].tornozelo = e.target.value;
                                  atualizaCampos("Direito", novos);
                                }}
                                style={{ width: "100%", padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                              />
                            </div>
                          </div>
                        </div>

                        {/* Segmento Insuficiente */}
                        <div>
                          <h6 style={{ color: "#0eb8d0", marginBottom: 8 }}>Segmento Insuficiente:</h6>
                          <div style={{ display: "grid", gridTemplateColumns: "1fr auto 1fr", gap: 10, alignItems: "end" }}>
                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Início:</label>
                              <div style={{ display: "flex", gap: 5 }}>
                                <input
                                  type="number"
                                  placeholder="Valor"
                                  value={camposD.safena["Safena Magna"].inicio_valor}
                                  onChange={e => {
                                    const novos = { ...camposD };
                                    novos.safena["Safena Magna"].inicio_valor = e.target.value;
                                    atualizaCampos("Direito", novos);
                                  }}
                                  style={{ flex: 1, padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                                />
                                <select
                                  value={camposD.safena["Safena Magna"].inicio}
                                  onChange={e => {
                                    const novos = { ...camposD };
                                    novos.safena["Safena Magna"].inicio = e.target.value;
                                    atualizaCampos("Direito", novos);
                                  }}
                                  style={{ padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
