                            </div>
                            <div style={{ textAlign: "center", color: "#aaa" }}>até</div>
                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Fim:</label>
                              <div style={{ display: "flex", gap: 5 }}>
                                <input
                                  type="number"
                                  placeholder="Valor"
                                  value={camposD.safena["Safena Magna"].fim_valor}
                                  onChange={e => {
                                    const novos = { ...camposD };
                                    novos.safena["Safena Magna"].fim_valor = e.target.value;
                                    atualizaCampos("Direito", novos);
                                  }}
                                  style={{ flex: 1, padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                                />
                                <select
                                  value={camposD.safena["Safena Magna"].fim}
                                  onChange={e => {
                                    const novos = { ...camposD };
                                    novos.safena["Safena Magna"].fim = e.target.value;
                                    atualizaCampos("Direito", novos);
                                  }}
                                  style={{ padding: 5, borderRadius: 4, border: "none", fontSize: 14 }}
                                >
                                  <option value="">Selecione</option>
                                  <option value="JSF">JSF</option>
                                  <option value="joelho">Joelho</option>
                                  <option value="tornozelo">Tornozelo</option>
                                </select>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Campos específicos para Safena Parva quando insuficiente */}
                    {veia === "Safena Parva" && camposD.superficial[veia] === "pérvia e insuficiente" && (
                      <div style={{ marginLeft: 20, marginBottom: 15, padding: 15, background: "#1a2332", borderRadius: 8 }}>
                        <h5 style={{ color: "#11b581", marginBottom: 10 }}>Dados da Safena Parva</h5>

                        {/* Diâmetros */}
                        <div style={{ marginBottom: 15 }}>
                          <h6 style={{ color: "#0eb8d0", marginBottom: 8 }}>Diâmetros (mm):</h6>
