                            <div>
                              <label style={{ fontSize: 12, color: "#aaa" }}>Proximal:</label>
                              <input
                                type="number"
                                placeholder="mm"
                                value={camposD.safena["Safena Parva"].proximal}
                                onChange={e => {
                                  const novos = { ...camposD };
                                  novos.safena["Safena Parva"].proximal = e.target.value;
                                  atualizaCampos("Direito", novos);
