import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { PDFDocument, rgb, StandardFonts } from "pdf-lib";
import { saveAs } from "file-saver";
import { FiArrowLeft } from "react-icons/fi";

// Helpers para exames salvos (localStorage)
const STORAGE_KEY = "examesMMIIVenoso";
function getExamesSalvos() {
  return JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
}
function salvarExame(exame) {
  const todos = getExamesSalvos();
  todos.push(exame);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
}
function atualizarExames(novos) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(novos));
}

export default function MMIIVenoso() {
  const navigate = useNavigate();

  // Campos do exame
  const [nome, setNome] = useState("");
  const [data, setData] = useState("");
  const [lado, setLado] = useState("");
  const [dados, setDados] = useState({});
  const [laudo, setLaudo] = useState("");
  const [mostrarExamesSalvos, setMostrarExamesSalvos] = useState(false);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  // Campos das veias
  const veiasProfundas = [
    "Veia Femoral Comum", "Veia Femoral Superficial", "Veia Femoral Profunda",
    "Veia Poplítea", "Veias Tibiais posteriores", "Veias Tibiais anteriores",
    "Veias Gastrocnêmicas", "Veias Soleares"
  ];
  const veiasSuperficiais = ["JSF", "Safena Magna", "JSP", "Safena Parva"];
  const ladosPossiveis = ["Direito", "Esquerdo", "Ambos"];

  // Estado dos campos das veias
  function initCampos() {
    return {
      profundo: Object.fromEntries(veiasProfundas.map(v => [v, "pérvia e competente"])),
      superficial: Object.fromEntries(veiasSuperficiais.map(v => [v, "pérvia e competente"])),
      safena: {
        "Safena Magna": {
          coxa: "", perna: "", tornozelo: "",
          inicio: "", inicio_valor: "", fim: "", fim_valor: ""
        },
        "Safena Parva": {
          proximal: "", distal: "",
          inicio: "", inicio_valor: "", fim: "", fim_valor: ""
        }
      },
      perfurante: "competentes",
      local_perfurante_valor: "",
      local_perfurante_tipo: "",
      obs: "",
    };
  }
  const [camposD, setCamposD] = useState(initCampos());
  const [camposE, setCamposE] = useState(initCampos());

  function handleLadoChange(val) {
    setLado(val);
    if (val === "Direito") setDados({ "Direito": { ...camposD } });
    else if (val === "Esquerdo") setDados({ "Esquerdo": { ...camposE } });
    else setDados({ "Direito": { ...camposD }, "Esquerdo": { ...camposE } });
  }
  function atualizaCampos(side, novos) {
    if (side === "Direito") { setCamposD(novos); setDados({ ...dados, Direito: novos }); }
    else { setCamposE(novos); setDados({ ...dados, Esquerdo: novos }); }
  }

  // Gerador de Laudo
  function gerarLaudo() {
    if (!nome || !data || !lado) {
      alert("Preencha nome, data e lado!");
      return;
    }
    let laudos = {};
    const sides = (lado === "Ambos") ? ["Direito", "Esquerdo"] : [lado];
    sides.forEach(side => {
      const c = side === "Direito" ? camposD : camposE;
      let txt = "";
      txt += `PACIENTE: ${nome}\n`;
      txt += `DATA: ${data}\n`;
      txt += `DOPPLER VENOSO DE MEMBRO INFERIOR ${side.toUpperCase()}\n\n`;
      txt += "Sistema Venoso Profundo:\n";
      veiasProfundas.forEach(v =>
        txt += `- ${v}: ${c.profundo[v]}\n`
      );
      txt += "\nSistema Venoso Superficial:\n";
      veiasSuperficiais.forEach(v => {
        txt += `- ${v}: ${c.superficial[v]}\n`;
        if (["Safena Magna", "Safena Parva"].includes(v) && c.superficial[v] === "pérvia e insuficiente") {
          const s = c.safena[v];
          if (v === "Safena Magna") {
            txt += `  > Diâmetro - Coxa: ${s.coxa} mm\n`;
            txt += `  > Diâmetro - Perna: ${s.perna} mm\n`;
            txt += `  > Diâmetro - Tornozelo: ${s.tornozelo} mm\n`;
          } else {
            txt += `  > Diâmetro Proximal: ${s.proximal} mm\n`;
            txt += `  > Diâmetro Distal: ${s.distal} mm\n`;
          }
          txt += `  > Segmento insuficiente: de ${s.inicio_valor} ${s.inicio} até ${s.fim_valor} ${s.fim}\n`;
        }
      });
      txt += `\nVeias Perfurantes: ${c.perfurante}\n`;
      if (c.perfurante === "incompetentes" && (c.local_perfurante_valor || c.local_perfurante_tipo)) {
        txt += `  > Localização: ${c.local_perfurante_valor} ${c.local_perfurante_tipo}\n`;
      }
      if (c.obs && c.obs.trim()) {
        txt += `\nObservações (${side}):\n${c.obs}\n`;
      }
      txt += `\nCONCLUSÃO:\n`;
      txt += gerarConclusaoPorSide(c, side);
      laudos[side] = txt;
    });
    setLaudo(Object.values(laudos).join("\n\n"));
    return laudos;
  }

  function gerarConclusaoPorSide(c, side) {
    const profundos = c.profundo;
    const superficiais = c.superficial;
    const segmentos = c.safena;
    const perfurante = c.perfurante;
    const local_perf = `${c.local_perfurante_valor} ${c.local_perfurante_tipo}`;
    let conclusoes = [];
    if (Object.values(profundos).some(v => v.includes("não compressível"))) {
      conclusoes.push("Trombose venosa profunda");
    }
    if (Object.values(profundos).some(v => v.includes("semi compressível"))) {
      conclusoes.push("Sinais de recanalização parcial do sistema venoso profundo");
    }
    if (Object.values(profundos).some(v => v.includes("incompetente"))) {
      conclusoes.push("Insuficiência de sistema venoso profundo");
    }
    ["Safena Magna", "Safena Parva"].forEach(veia => {
      if (superficiais[veia] === "pérvia e insuficiente") {
        const ini = segmentos[veia]["inicio"];
        const fim = segmentos[veia]["fim"];
        const ini_val = segmentos[veia]["inicio_valor"];
        const fim_val = segmentos[veia]["fim_valor"];
        const ini_fmt = (ini && ini.includes("cm") && ini_val) ? `${ini_val} ${ini}` : ini;
        const fim_fmt = (fim && fim.includes("cm") && fim_val) ? `${fim_val} ${fim}` : fim;
        if ((ini_fmt && ini_fmt.includes("JSF") && fim_fmt && fim_fmt.includes("tornozelo")) ||
            (ini_fmt && ini_fmt.includes("JSP") && fim_fmt && fim_fmt.includes("tornozelo"))) {
          conclusoes.push(`Insuficiência total da ${veia.toLowerCase()}`);
        } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("tornozelo")) {
          conclusoes.push(`Insuficiência parcial da ${veia.toLowerCase()}`);
        } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("joelho")) {
          conclusoes.push(`Insuficiência segmentar da ${veia.toLowerCase()}`);
        }
      }
    });
    if (superficiais["Safena Magna"] === "não compressível e sem fluxo (trombose)") {
      conclusoes.push("Tromboflebite da safena magna");
    }
    if (superficiais["Safena Parva"] === "não compressível e sem fluxo (trombose)") {
      conclusoes.push("Tromboflebite da safena parva");
    }
    if (superficiais["JSF"] === "pérvia e insuficiente") {
      conclusoes.push("Incompetência da junção safeno-femoral (JSF)");
    }
    if (superficiais["JSP"] === "pérvia e insuficiente") {
      conclusoes.push("Incompetência da junção safeno-poplítea (JSP)");
    }
    if (perfurante === "incompetentes") {
      if (local_perf.trim()) conclusoes.push(`Insuficiência de veias perfurantes (${local_perf})`);
      else conclusoes.push("Insuficiência de veias perfurantes");
    }
    if (!conclusoes.length) return "- Ausência de refluxo venoso nos territórios estudados.";
    return "- " + conclusoes.join("\n- ");
  }

  // PDF
  async function gerarPDFExame({ nome, data, lados, laudosPorLado }) {
    const doc = await PDFDocument.create();
    const font = await doc.embedFont(StandardFonts.Helvetica);
    const fontBold = await doc.embedFont(StandardFonts.HelveticaBold);
    lados.forEach(ladoX => {
      const laudo = laudosPorLado[ladoX];
      const page = doc.addPage([595, 842]); // A4 Portrait
      let y = 800;
      const left = 50;
      const lineHeight = 20;
      // PACIENTE (negrito)
      page.drawText(`PACIENTE: ${nome}`, { x: left, y, font: fontBold, size: 13, color: rgb(0,0,0) }); y -= lineHeight;
      page.drawText(`DATA: ${data}`, { x: left, y, font: font, size: 12, color: rgb(0,0,0) }); y -= lineHeight;
      page.drawText(`DOPPLER VENOSO DE MEMBRO INFERIOR ${ladoX.toUpperCase()}`, { x: left, y, font: fontBold, size: 13, color: rgb(0,0,0) }); y -= lineHeight*1.2;
      // Corpo
      const drawSection = (titulo, texto) => {
        page.drawText(titulo, { x: left, y, font: fontBold, size: 12, color: rgb(0,0,0) }); y -= lineHeight*1.1;
        texto.split("\n").forEach(line => {
          page.drawText(line, { x: left+12, y, font: font, size: 12, color: rgb(0,0,0) }); y -= lineHeight*0.95;
          if (y < 70) { y = 800; doc.addPage([595, 842]); }
        }); y -= 7;
      };
      const prof = laudo.match(/Sistema Venoso Profundo:\n([\s\S]+?)\n\n/)?.[1] || "";
      const superf = laudo.match(/Sistema Venoso Superficial:\n([\s\S]+?)\n\n/)?.[1] || "";
      const perf = laudo.match(/Veias Perfurantes:[\s\S]+?\n\n/)?.[0]?.replace(/^\s+|\s+$/g,"") || "";
      const obs = laudo.match(/Observações.+?:\n([\s\S]+?)\n\n/)?.[1] || "";
      const conclusao = laudo.match(/CONCLUSÃO:\n([\s\S]+)/)?.[1] || "";
      drawSection("Sistema Venoso Profundo", prof);
      drawSection("Sistema Venoso Superficial", superf);
      if (perf) drawSection("Veias Perfurantes", perf);
      if (obs) drawSection("Observações", obs);
      // Conclusão
      if (conclusao) {
        y -= 6;
        page.drawText("CONCLUSÃO:", { x: left, y, font: fontBold, size: 13, color: rgb(0,0,0) }); y -= lineHeight*1.1;
        conclusao.split("\n").forEach(line => {
          page.drawText(line, { x: left+12, y, font: fontBold, size: 12, color: rgb(0,0,0) }); y -= lineHeight*0.95;
          if (y < 70) { y = 800; doc.addPage([595,842]); }
        });
      }
    });
    const pdfBytes = await doc.save();
    const nomeArquivo = `Laudo_${nome.replace(/\s+/g,"_")}_${data}.pdf`;
    saveAs(new Blob([pdfBytes], { type: "application/pdf" }), nomeArquivo);
  }

  // Salvar exame
  function handleSalvarExame() {
    const laudosPorLado = gerarLaudo();
    if (!laudosPorLado) return;
    const exameSalvo = {
      id: Date.now() + "_" + Math.random().toString(36).slice(2,7),
      nome,
      data,
      lado,
      laudo: Object.values(laudosPorLado).join("\n\n"),
      laudosPorLado,
      dados: JSON.parse(JSON.stringify(dados)),
      criadoEm: new Date().toISOString(),
    };
    salvarExame(exameSalvo);
    gerarPDFExame({ nome, data, lados: Object.keys(laudosPorLado), laudosPorLado });
    alert("Exame salvo e PDF gerado com sucesso!");
  }

  // Visualização HTML com negrito
  function laudoHTMLView() {
    if (!laudo) return <div style={{ color: "#888", fontSize: 17 }}>Preencha os campos e clique em "Visualizar Laudo"</div>;
    return laudo.split("\n").map((l,i) => {
      if (l.startsWith("PACIENTE:") || l.startsWith("DOPPLER VENOSO") || l.startsWith("Sistema Venoso Profundo") || l.startsWith("Sistema Venoso Superficial") || l.startsWith("CONCLUSÃO")) {
        return <div key={i} style={{ fontWeight: "bold", color: "#0eb8d0", fontSize: 18 }}>{l}</div>;
      }
      if (l.startsWith("DATA:")) {
        return <div key={i} style={{ fontWeight: "600", color: "#aaffee" }}>{l}</div>;
      }
      return <div key={i} style={{ whiteSpace: "pre-wrap" }}>{l}</div>;
    });
  }

  // Render
  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(120deg,#101824 0%,#1c2740 100%)",
      color: "#fff",
      fontFamily: "Segoe UI, Inter, Arial, sans-serif",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      padding: 0,
      paddingTop: 28,
    }}>
      <div style={{ position: "absolute", left: 22, top: 22 }}>
        <button onClick={() => navigate(-1)}
          style={{
            background: "#232f4e", color: "#0eb8d0", border: "2px solid #0eb8d0",
            borderRadius: 8, fontSize: 18, padding: "7px 16px", fontWeight: 700, cursor: "pointer",
            display: "flex", alignItems: "center", gap: 7
          }}
        >
          <FiArrowLeft size={22}/> Voltar
        </button>
      </div>
      <h2 style={{ fontSize: 26, fontWeight: 800, letterSpacing: 1, marginTop: 10, color: "#0eb8d0" }}>
        Gerador de Laudo Doppler MMII Venoso
      </h2>
      
      {/* Dados Básicos */}
      <div style={{
        background: "#242d43",
        borderRadius: 14,
        padding: "24px 28px",
        minWidth: 320,
        marginTop: 26,
        boxShadow: "0 2px 16px #0eb8d055"
      }}>
        <div style={{ display: "flex", gap: 18, marginBottom: 16, flexWrap: "wrap" }}>
          <div>
            <div style={{ fontWeight: 600 }}>Nome do Paciente:</div>
            <input type="text" value={nome} onChange={e => setNome(e.target.value)}
              style={{ padding: 7, borderRadius: 6, border: "none", width: 210, fontSize: 16, marginTop: 2 }} />
          </div>
          <div>
            <div style={{ fontWeight: 600 }}>Data:</div>
            <input type="date" value={data} onChange={e => setData(e.target.value)}
              style={{ padding: 7, borderRadius: 6, border: "none", width: 150, fontSize: 16, marginTop: 2 }} />
          </div>
          <div>
            <div style={{ fontWeight: 600 }}>Lado:</div>
            <select value={lado} onChange={e => handleLadoChange(e.target.value)}
              style={{ padding: 7, borderRadius: 6, border: "none", width: 120, fontSize: 16, marginTop: 2 }}>
              <option value="">Selecione</option>
              {ladosPossiveis.map(l => <option key={l} value={l}>{l}</option>)}
            </select>
          </div>
        </div>
        
        {nome && data && lado && (
          <button
            onClick={() => setMostrarFormulario(true)}
            style={{
              background: "#0eb8d0",
              color: "#fff",
              border: "none",
              borderRadius: 8,
              padding: "12px 24px",
              fontWeight: 600,
              fontSize: 16,
              cursor: "pointer",
              marginTop: 10
            }}
          >
            Continuar para Preenchimento do Exame
          </button>
        )}
      </div>

      {/* Formulário Completo */}
      {mostrarFormulario && (
        <div style={{
          background: "#242d43",
          borderRadius: 14,
          padding: "24px 28px",
          minWidth: 320,
          marginTop: 20,
          boxShadow: "0 2px 16px #0eb8d055",
          maxWidth: "90vw",
          overflowX: "auto"
        }}>
          {lado === "Ambos" ? (
            // Formulário para Ambos os Membros
            <div>
              {/* Membro Direito */}
              <div style={{ marginBottom: 40, borderBottom: "2px solid #0eb8d0", paddingBottom: 20 }}>
                <h3 style={{ color: "#0eb8d0", marginBottom: 20, textAlign: "center", fontSize: 20 }}>
                  🦵 MEMBRO INFERIOR DIREITO
                </h3>
                
                {/* Sistema Venoso Profundo - Direito */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Profundo</h4>
                  {veiasProfundas.map(veia => (
                    <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                      <span style={{ minWidth: 200 }}>{veia}:</span>
                      <select
                        value={camposD.profundo[veia]}
                        onChange={e => {
                          const novos = { ...camposD };
                          novos.profundo[veia] = e.target.value;
                          atualizaCampos("Direito", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      >
                        <option value="pérvia e competente">Pérvia e competente</option>
                        <option value="pérvia e incompetente">Pérvia e incompetente</option>
                        <option value="não compressível">Não compressível</option>
                        <option value="semi compressível">Semi compressível</option>
                      </select>
                    </div>
                  ))}
                </div>

                {/* Sistema Venoso Superficial - Direito */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Superficial</h4>
                  {veiasSuperficiais.map(veia => (
                    <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                      <span style={{ minWidth: 200 }}>{veia}:</span>
                      <select
                        value={camposD.superficial[veia]}
                        onChange={e => {
                          const novos = { ...camposD };
                          novos.superficial[veia] = e.target.value;
                          atualizaCampos("Direito", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      >
                        <option value="pérvia e competente">Pérvia e competente</option>
                        <option value="pérvia e insuficiente">Pérvia e insuficiente</option>
                        <option value="não compressível e sem fluxo (trombose)">Não compressível e sem fluxo (trombose)</option>
                      </select>
                    </div>
                  ))}
                </div>

                {/* Veias Perfurantes - Direito */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Veias Perfurantes</h4>
                  <div style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 10 }}>
                    <span>Status:</span>
                    <select
                      value={camposD.perfurante}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.perfurante = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    >
                      <option value="competentes">Competentes</option>
                      <option value="incompetentes">Incompetentes</option>
                    </select>
                  </div>
                  {camposD.perfurante === "incompetentes" && (
                    <div style={{ display: "flex", gap: 10 }}>
                      <input
                        type="text"
                        placeholder="Localização"
                        value={camposD.local_perfurante_valor}
                        onChange={e => {
                          const novos = { ...camposD };
                          novos.local_perfurante_valor = e.target.value;
                          atualizaCampos("Direito", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      />
                      <input
                        type="text"
                        placeholder="Tipo"
                        value={camposD.local_perfurante_tipo}
                        onChange={e => {
                          const novos = { ...camposD };
                          novos.local_perfurante_tipo = e.target.value;
                          atualizaCampos("Direito", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      />
                    </div>
                  )}
                </div>

                {/* Observações - Direito */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Observações (Direito)</h4>
                  <textarea
                    value={camposD.obs}
                    onChange={e => {
                      const novos = { ...camposD };
                      novos.obs = e.target.value;
                      atualizaCampos("Direito", novos);
                    }}
                    placeholder="Observações específicas do membro direito..."
                    style={{ 
                      width: "100%", 
                      padding: 10, 
                      borderRadius: 6, 
                      border: "none", 
                      minHeight: 80,
                      resize: "vertical"
                    }}
                  />
                </div>
              </div>

              {/* Membro Esquerdo */}
              <div style={{ marginBottom: 40 }}>
                <h3 style={{ color: "#0eb8d0", marginBottom: 20, textAlign: "center", fontSize: 20 }}>
                  🦵 MEMBRO INFERIOR ESQUERDO
                </h3>
                
                {/* Sistema Venoso Profundo - Esquerdo */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Profundo</h4>
                  {veiasProfundas.map(veia => (
                    <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                      <span style={{ minWidth: 200 }}>{veia}:</span>
                      <select
                        value={camposE.profundo[veia]}
                        onChange={e => {
                          const novos = { ...camposE };
                          novos.profundo[veia] = e.target.value;
                          atualizaCampos("Esquerdo", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      >
                        <option value="pérvia e competente">Pérvia e competente</option>
                        <option value="pérvia e incompetente">Pérvia e incompetente</option>
                        <option value="não compressível">Não compressível</option>
                        <option value="semi compressível">Semi compressível</option>
                      </select>
                    </div>
                  ))}
                </div>

                {/* Sistema Venoso Superficial - Esquerdo */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Superficial</h4>
                  {veiasSuperficiais.map(veia => (
                    <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                      <span style={{ minWidth: 200 }}>{veia}:</span>
                      <select
                        value={camposE.superficial[veia]}
                        onChange={e => {
                          const novos = { ...camposE };
                          novos.superficial[veia] = e.target.value;
                          atualizaCampos("Esquerdo", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      >
                        <option value="pérvia e competente">Pérvia e competente</option>
                        <option value="pérvia e insuficiente">Pérvia e insuficiente</option>
                        <option value="não compressível e sem fluxo (trombose)">Não compressível e sem fluxo (trombose)</option>
                      </select>
                    </div>
                  ))}
                </div>

                {/* Veias Perfurantes - Esquerdo */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Veias Perfurantes</h4>
                  <div style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 10 }}>
                    <span>Status:</span>
                    <select
                      value={camposE.perfurante}
                      onChange={e => {
                        const novos = { ...camposE };
                        novos.perfurante = e.target.value;
                        atualizaCampos("Esquerdo", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    >
                      <option value="competentes">Competentes</option>
                      <option value="incompetentes">Incompetentes</option>
                    </select>
                  </div>
                  {camposE.perfurante === "incompetentes" && (
                    <div style={{ display: "flex", gap: 10 }}>
                      <input
                        type="text"
                        placeholder="Localização"
                        value={camposE.local_perfurante_valor}
                        onChange={e => {
                          const novos = { ...camposE };
                          novos.local_perfurante_valor = e.target.value;
                          atualizaCampos("Esquerdo", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      />
                      <input
                        type="text"
                        placeholder="Tipo"
                        value={camposE.local_perfurante_tipo}
                        onChange={e => {
                          const novos = { ...camposE };
                          novos.local_perfurante_tipo = e.target.value;
                          atualizaCampos("Esquerdo", novos);
                        }}
                        style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                      />
                    </div>
                  )}
                </div>

                {/* Observações - Esquerdo */}
                <div style={{ marginBottom: 30 }}>
                  <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Observações (Esquerdo)</h4>
                  <textarea
                    value={camposE.obs}
                    onChange={e => {
                      const novos = { ...camposE };
                      novos.obs = e.target.value;
                      atualizaCampos("Esquerdo", novos);
                    }}
                    placeholder="Observações específicas do membro esquerdo..."
                    style={{ 
                      width: "100%", 
                      padding: 10, 
                      borderRadius: 6, 
                      border: "none", 
                      minHeight: 80,
                      resize: "vertical"
                    }}
                  />
                </div>
              </div>
            </div>
          ) : (
            // Formulário para um membro específico
            <div>
              <h3 style={{ color: "#0eb8d0", marginBottom: 20, textAlign: "center" }}>
                Preenchimento do Exame - {lado}
              </h3>
              
              {/* Sistema Venoso Profundo */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Profundo</h4>
                {veiasProfundas.map(veia => (
                  <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                    <span style={{ minWidth: 200 }}>{veia}:</span>
                    <select
                      value={camposD.profundo[veia]}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.profundo[veia] = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    >
                      <option value="pérvia e competente">Pérvia e competente</option>
                      <option value="pérvia e incompetente">Pérvia e incompetente</option>
                      <option value="não compressível">Não compressível</option>
                      <option value="semi compressível">Semi compressível</option>
                    </select>
                  </div>
                ))}
              </div>

              {/* Sistema Venoso Superficial */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Sistema Venoso Superficial</h4>
                {veiasSuperficiais.map(veia => (
                  <div key={veia} style={{ marginBottom: 10, display: "flex", alignItems: "center", gap: 10 }}>
                    <span style={{ minWidth: 200 }}>{veia}:</span>
                    <select
                      value={camposD.superficial[veia]}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.superficial[veia] = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    >
                      <option value="pérvia e competente">Pérvia e competente</option>
                      <option value="pérvia e insuficiente">Pérvia e insuficiente</option>
                      <option value="não compressível e sem fluxo (trombose)">Não compressível e sem fluxo (trombose)</option>
                    </select>
                  </div>
                ))}
              </div>

              {/* Veias Perfurantes */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Veias Perfurantes</h4>
                <div style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 10 }}>
                  <span>Status:</span>
                  <select
                    value={camposD.perfurante}
                    onChange={e => {
                      const novos = { ...camposD };
                      novos.perfurante = e.target.value;
                      atualizaCampos("Direito", novos);
                    }}
                    style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                  >
                    <option value="competentes">Competentes</option>
                    <option value="incompetentes">Incompetentes</option>
                  </select>
                </div>
                {camposD.perfurante === "incompetentes" && (
                  <div style={{ display: "flex", gap: 10 }}>
                    <input
                      type="text"
                      placeholder="Localização"
                      value={camposD.local_perfurante_valor}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.local_perfurante_valor = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    />
                    <input
                      type="text"
                      placeholder="Tipo"
                      value={camposD.local_perfurante_tipo}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.local_perfurante_tipo = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    />
                  </div>
                )}
              </div>

              {/* Observações */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Observações</h4>
                <textarea
                  value={camposD.obs}
                  onChange={e => {
                    const novos = { ...camposD };
                    novos.obs = e.target.value;
                    atualizaCampos("Direito", novos);
                  }}
                  placeholder="Observações específicas..."
                  style={{ 
                    width: "100%", 
                    padding: 10, 
                    borderRadius: 6, 
                    border: "none", 
                    minHeight: 80,
                    resize: "vertical"
                  }}
                />
              </div>
            </div>
          )}

          {/* Botões de Ação */}
          <div style={{ display: "flex", gap: 15, justifyContent: "center", marginTop: 30 }}>
            <button
              onClick={gerarLaudo}
              style={{
                background: "#11b581",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                padding: "12px 24px",
                fontWeight: 600,
                fontSize: 16,
                cursor: "pointer"
              }}
            >
              Visualizar Laudo
            </button>
            <button
              onClick={handleSalvarExame}
              style={{
                background: "#0eb8d0",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                padding: "12px 24px",
                fontWeight: 600,
                fontSize: 16,
                cursor: "pointer"
              }}
            >
              Salvar e Gerar PDF
            </button>
          </div>

          {/* Visualização do Laudo */}
          {laudo && (
            <div style={{ marginTop: 30, padding: 20, background: "#1a2332", borderRadius: 10 }}>
              <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Prévia do Laudo:</h4>
              <div style={{ 
                background: "#fff", 
                color: "#000", 
                padding: 15, 
                borderRadius: 8, 
                maxHeight: 400, 
                overflowY: "auto",
                whiteSpace: "pre-wrap",
                fontFamily: "monospace",
                fontSize: 14
              }}>
                {laudoHTMLView()}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
