    conclusoes.push("Tromboflebite da safena parva");
  }
  if (superficiais["JSF"] === "pérvia e insuficiente") {
    conclusoes.push("Incompetência da junção safeno-femoral (JSF)");
  }
  if (superficiais["JSP"] === "pérvia e insuficiente") {
    conclusoes.push("Incompetência da junção safeno-poplítea (JSP)");
  }
  if (!conclusoes.length) return "- Ausência de refluxo venoso nos territórios estudados.";
  return "- " + conclusoes.join("\n- ");
}
function montarLaudo({
  nome,
  data,
  lado,
  profundas,
  superficiais,
  magna,
  parva,
  jsfDiametro,
  jspDiametro
}) {
  const lados = lado === "ambos" ? ["Direito", "Esquerdo"] : [lado.charAt(0).toUpperCase() + lado.slice(1)];
  const blocos = lados.map(l => {
    let linhas = [];
    linhas.push(`PACIENTE: ${nome}`);
    linhas.push(`DATA: ${data}`);
    linhas.push(`DOPPLER VENOSO DE MEMBRO INFERIOR ${l.toUpperCase()}`);
    linhas.push("");
    linhas.push("Sistema Venoso Profundo:");
    veiasProfundas.forEach(v => {
      linhas.push(`- ${v}: ${profundas[l][v]}`);
    });
    linhas.push("");
    linhas.push("Sistema Venoso Superficial:");
    veiasSuperficiais.forEach(v => {
      let extra = "";
      if (v === "JSF" && jsfDiametro && jsfDiametro[l]) extra = ` (diâmetro: ${jsfDiametro[l]} mm)`;
      if (v === "JSP" && jspDiametro && jspDiametro[l]) extra = ` (diâmetro: ${jspDiametro[l]} mm)`;
      linhas.push(`- ${v}: ${superficiais[l][v]}${extra}`);
      if (v === "Safena Magna" && superficiais[l][v] === "pérvia e insuficiente") {
        linhas.push(`  > Diâmetro - Coxa: ${magna[l].coxa} mm`);
        linhas.push(`  > Diâmetro - Perna: ${magna[l].perna} mm`);
        linhas.push(`  > Diâmetro - Tornozelo: ${magna[l].tornozelo} mm`);
        linhas.push(`  > Segmento insuficiente: de ${magna[l].inicio_valor} ${magna[l].inicio} até ${magna[l].fim_valor} ${magna[l].fim}`);
      }
      if (v === "Safena Parva" && superficiais[l][v] === "pérvia e insuficiente") {
        linhas.push(`  > Diâmetro Proximal: ${parva[l].proximal} mm`);
        linhas.push(`  > Diâmetro Distal: ${parva[l].distal} mm`);
        linhas.push(`  > Segmento insuficiente: de ${parva[l].inicio_valor} ${parva[l].inicio} até ${parva[l].fim_valor} ${parva[l].fim}`);
      }
    });
    linhas.push("");
    linhas.push("CONCLUSÃO:");
    linhas.push(gerarConclusaoPorLado({
      profundas: profundas[l],
      superficiais: superficiais[l],
      magna: magna[l],
      parva: parva[l]
    }));
    return linhas.join("\n");
  });
  return blocos.join("\n\n" + "=".repeat(80) + "\n\n");
}
