import React, { useState } from "react";
import { useRef, useState as useReactState } from "react";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";

// Listas de veias e opções
const veiasProfundas = [
  "Veia Femoral Comum",
  "Veia Femoral Superficial",
  "Veia Femoral Profunda",
  "Veia Poplítea",
  "Veias Tibiais posteriores",
  "Veias Tibiais anteriores",
  "Veias Gastrocnêmicas",
  "Veias Soleares",
];

const veiasSuperficiais = [
  "JSF",
  "Safena Magna",
  "JSP",
  "Safena Parva",
];

const profOptions = [
  "pérvia e competente",
  "pérvia e incompetente",
  "não compressível e sem fluxo (sugestivo de trombose)",
  "semi compressível, sugestivo de recanalização parcial",
];

const supOptions = [
  "pérvia e competente",
  "pérvia e insuficiente",
  "não compressível e sem fluxo (trombose)",
  "ausente"
];

const lados = ["Direito", "Esquerdo", "Ambos"];

// Novo componente para bloco de campos de um lado
function BlocoCampos({ lado, profundas, superficiais, magna, parva, onProfundas, onSuperficiais, onMagna, onParva, jsfDiametro, setJsfDiametro, jspDiametro, setJspDiametro }) {
  return (
    <div style={{marginBottom: 24, width: '100%', maxWidth: 500, minWidth: 320, display: 'flex', flexDirection: 'column', gap: 18}}>
      <div style={{marginTop:14,marginBottom:8,fontWeight:700}}>
        Sistema Venoso Profundo ({lado}):
      </div>
      {veiasProfundas.map(veia => (
        <div key={veia} style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6 }}>
          <label style={{ minWidth: 180 }}>{veia}:</label>
          <select value={profundas[veia]} onChange={e=>onProfundas({...profundas, [veia]:e.target.value})} style={{width:260, padding:6, borderRadius:6}}>
            {profOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
        </div>
      ))}
      <div style={{marginTop:20,fontWeight:700}}>
        Sistema Venoso Superficial ({lado}):
      </div>
      {veiasSuperficiais.map(veia => (
        <div key={veia} style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 6 }}>
          <label style={{ minWidth: 120 }}>{veia}:</label>
          <select
            value={superficiais[veia]}
            onChange={e=>onSuperficiais({...superficiais, [veia]:e.target.value})}
            style={{width:200, padding:6, borderRadius:6}}
          >
            {supOptions.map(opt=><option key={opt}>{opt}</option>)}
          </select>
          {(veia === "JSF") && (
            <input type="number" min={0} step={0.1} placeholder="Diâmetro (mm)" value={jsfDiametro} onChange={e => setJsfDiametro(e.target.value)} style={{ width: 90, marginLeft: 8, borderRadius: 5, border: "1px solid #0eb8d0", padding: 6 }} />
          )}
          {(veia === "JSP") && (
            <input type="number" min={0} step={0.1} placeholder="Diâmetro (mm)" value={jspDiametro} onChange={e => setJspDiametro(e.target.value)} style={{ width: 90, marginLeft: 8, borderRadius: 5, border: "1px solid #0eb8d0", padding: 6 }} />
          )}
          {veia==="Safena Magna" &&
            <SafenaMagnaExtra status={superficiais[veia]} valores={magna} onChange={onMagna} />
          }
          {veia==="Safena Parva" &&
            <SafenaParvaExtra status={superficiais[veia]} valores={parva} onChange={onParva} />
          }
        </div>
      ))}
    </div>
  );
}

// Componente extra para Safena Magna insuficiente
