import React, { useState } from "react";

export default function MMIIVenoso() {
  const [form, setForm] = useState({
    nome: "",
    idade: "",
    data: "",
    lado: "direito"
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Aqui você pode adicionar lógica para salvar os dados
    alert("Exame cadastrado!\n" + JSON.stringify(form, null, 2));
  }

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(120deg,#101824 0%,#1c2740 100%)",
      color: "#fff",
      fontFamily: "Segoe UI, Inter, Arial, sans-serif",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      paddingTop: 30,
    }}>
      <div style={{
        background: "#18243a",
        borderRadius: 14,
        boxShadow: "0 4px 24px #00e0ff20",
        padding: "32px 28px 24px 28px",
        maxWidth: 420,
        width: "100%",
        marginTop: 30,
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
      }}>
        <h2 style={{
          color: "#0eb8d0",
          fontWeight: 700,
          fontSize: 22,
          marginBottom: 18,
          letterSpacing: 0.5
        }}>
          Cadastro de Exame Venoso MMII
        </h2>
        <form onSubmit={handleSubmit} style={{ width: "100%" }}>
          <div style={{ display: "flex", gap: 10, marginBottom: 16 }}>
            <input
              type="text"
              name="nome"
              placeholder="Nome do Paciente"
              value={form.nome}
              onChange={handleChange}
              required
              style={inputStyle}
              autoComplete="off"
            />
            <input
              type="number"
              name="idade"
              placeholder="Idade"
              value={form.idade}
              onChange={handleChange}
              min={0}
              max={120}
              required
              style={{ ...inputStyle, width: 80 }}
            />
          </div>
          <div style={{ display: "flex", gap: 10, marginBottom: 16 }}>
            <input
              type="date"
              name="data"
              placeholder="Data do Exame"
              value={form.data}
              onChange={handleChange}
              required
              style={{ ...inputStyle, width: 160 }}
            />
            <select
              name="lado"
              value={form.lado}
              onChange={handleChange}
              style={{ ...inputStyle, width: 120, color: form.lado ? "#222" : "#888" }}
              required
            >
              <option value="direito">Direito</option>
              <option value="esquerdo">Esquerdo</option>
              <option value="ambos">Ambos</option>
            </select>
          </div>
          <button
            type="submit"
            style={{
              width: "100%",
              background: "#0eb8d0",
              color: "#fff",
              fontWeight: 600,
              fontSize: 16,
              padding: "10px 0",
              border: "none",
              borderRadius: 8,
              cursor: "pointer",
              letterSpacing: 0.5,
              boxShadow: "0 2px 10px #00e0ff30",
              transition: ".2s",
              marginTop: 10
            }}
            onMouseOver={e => e.currentTarget.style.background = "#0ca8b8"}
            onMouseOut={e => e.currentTarget.style.background = "#0eb8d0"}
          >
            Cadastrar Exame
          </button>
        </form>
      </div>
    </div>
  );
}

const inputStyle = {
  background: "#f7fbff",
  border: "1.5px solid #0eb8d0",
  borderRadius: 7,
  padding: "8px 12px",
  fontSize: 15,
  color: "#222",
  outline: "none",
  fontFamily: "inherit",
  fontWeight: 500,
  width: "100%"
};
