      </div>
    </div>
  );
}

function gerarConclusaoPorLado({ profundas, superficiais, magna, parva, perfurantes, jsfDiametro, jspDiametro }) {
  const conclusoes = [];

  // Análise do Sistema Venoso Profundo
  const profundasAlteradas = Object.entries(profundas).filter(([veia, status]) => 
    status !== "pérvia e competente"
  );

  if (profundasAlteradas.length > 0) {
    const tromboses = profundasAlteradas.filter(([veia, status]) => 
      status.includes("não compressível")
    );
    const recanalizacoes = profundasAlteradas.filter(([veia, status]) => 
      status.includes("semi compressível")
    );
    const incompetentes = profundasAlteradas.filter(([veia, status]) => 
      status.includes("incompetente")
    );

    if (tromboses.length > 0) {
      if (tromboses.length === 1) {
        conclusoes.push(`Trombose venosa profunda da ${tromboses[0][0].toLowerCase()}`);
      } else {
        conclusoes.push(`Trombose venosa profunda de múltiplas veias: ${tromboses.map(([veia]) => veia.toLowerCase()).join(', ')}`);
      }
    }

    if (recanalizacoes.length > 0) {
      conclusoes.push("Sinais de recanalização parcial do sistema venoso profundo");
    }

    if (incompetentes.length > 0) {
      conclusoes.push("Insuficiência de sistema venoso profundo");
    }
  }

  // Análise do Sistema Venoso Superficial
  const superficiaisAlteradas = Object.entries(superficiais).filter(([veia, status]) => 
    status !== "pérvia e competente"
  );

  // JSF - Junção Safeno-Femoral
  if (superficiais["JSF"] === "pérvia e incompetente") {
    const diametro = jsfDiametro ? ` (diâmetro: ${jsfDiametro} mm)` : '';
    conclusoes.push(`Incompetência da junção safeno-femoral (JSF)${diametro}`);
  }

  // JSP - Junção Safeno-Poplítea
  if (superficiais["JSP"] === "pérvia e incompetente") {
    const diametro = jspDiametro ? ` (diâmetro: ${jspDiametro} mm)` : '';
    conclusoes.push(`Incompetência da junção safeno-poplítea (JSP)${diametro}`);
  }

  // Safena Magna
  if (superficiais["Safena Magna"] === "pérvia e incompetente") {
    const ini = magna.inicio;
    const fim = magna.fim;
    const ini_val = magna.inicio_valor;
    const fim_val = magna.fim_valor;
    const ini_fmt = ini && ini.startsWith("cm_") && ini_val ? `${ini_val} ${ini.replace("cm_", "cm ")}` : ini;
    const fim_fmt = fim && fim.startsWith("cm_") && fim_val ? `${fim_val} ${fim.replace("cm_", "cm ")}` : fim;

    let tipoInsuficiencia = "";
    if ((ini_fmt && ini_fmt.includes("JSF")) && (fim_fmt && fim_fmt.includes("tornozelo"))) {
      tipoInsuficiencia = "total";
    } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("tornozelo")) {
      tipoInsuficiencia = "parcial";
    } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("joelho")) {
      tipoInsuficiencia = "segmentar";
    }

    const diametros = [];
    if (magna.coxa) diametros.push(`coxa: ${magna.coxa}mm`);
    if (magna.perna) diametros.push(`perna: ${magna.perna}mm`);
    if (magna.tornozelo) diametros.push(`tornozelo: ${magna.tornozelo}mm`);

    const diametrosStr = diametros.length > 0 ? ` (diâmetros: ${diametros.join(', ')})` : '';
    const segmentoStr = ini_fmt && fim_fmt ? ` - segmento: de ${ini_fmt} até ${fim_fmt}` : '';

    conclusoes.push(`Insuficiência ${tipoInsuficiencia} da safena magna${diametrosStr}${segmentoStr}`);
  }

  if (superficiais["Safena Magna"] === "não compressível e sem fluxo (trombose)") {
    conclusoes.push("Tromboflebite da safena magna");
  }

  if (superficiais["Safena Magna"] === "ausente") {
    conclusoes.push("Safena magna ausente");
  }

  // Safena Parva
  if (superficiais["Safena Parva"] === "pérvia e incompetente") {
    const ini = parva.inicio;
    const fim = parva.fim;
    const ini_val = parva.inicio_valor;
    const fim_val = parva.fim_valor;
    const ini_fmt = ini && ini.startsWith("cm_") && ini_val ? `${ini_val} ${ini.replace("cm_", "cm ")}` : ini;
    const fim_fmt = fim && fim.startsWith("cm_") && fim_val ? `${fim_val} ${fim.replace("cm_", "cm ")}` : fim;

    let tipoInsuficiencia = "";
    if ((ini_fmt && ini_fmt.includes("JSP")) && (fim_fmt && fim_fmt.includes("tornozelo"))) {
      tipoInsuficiencia = "total";
    } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("tornozelo")) {
      tipoInsuficiencia = "parcial";
    } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("joelho")) {
      tipoInsuficiencia = "segmentar";
    }

    const diametros = [];
    if (parva.proximal) diametros.push(`proximal: ${parva.proximal}mm`);
    if (parva.distal) diametros.push(`distal: ${parva.distal}mm`);

    const diametrosStr = diametros.length > 0 ? ` (diâmetros: ${diametros.join(', ')})` : '';
    const segmentoStr = ini_fmt && fim_fmt ? ` - segmento: de ${ini_fmt} até ${fim_fmt}` : '';

    conclusoes.push(`Insuficiência ${tipoInsuficiencia} da safena parva${diametrosStr}${segmentoStr}`);
  }

  if (superficiais["Safena Parva"] === "não compressível e sem fluxo (trombose)") {
    conclusoes.push("Tromboflebite da safena parva");
  }

  if (superficiais["Safena Parva"] === "ausente") {
    conclusoes.push("Safena parva ausente");
  }

  // Veias Perfurantes
  if (perfurantes && perfurantes.status && perfurantes.status !== "pérvia e competente") {
    if (perfurantes.status === "pérvia e incompetente") {
      const localizacao = perfurantes.segmento && perfurantes.valor ? 
        ` localizada ${perfurantes.valor} ${perfurantes.segmento}` : '';
      conclusoes.push(`Incompetência das veias perfurantes${localizacao}`);
    }
  }

  // Conclusão final
  if (!conclusoes.length) {
    return "- Ausência de refluxo venoso nos territórios estudados.";
  }

  return "- " + conclusoes.join("\n- ");
}

function montarLaudo({ nome, data, lado, profundas, superficiais, magna, parva, jsfDiametro, jspDiametro, observacoes, perfurantes }) {
  const lados = lado === "ambos" ? ["Direito", "Esquerdo"] : [lado.charAt(0).toUpperCase() + lado.slice(1)];
  const blocos = lados.map(l => {
    let linhas = [];
    linhas.push(`PACIENTE: ${nome}`);
    linhas.push(`DATA: ${data}`);
    linhas.push(`DOPPLER VENOSO DE MEMBRO INFERIOR ${l.toUpperCase()}`);
    linhas.push("");
    linhas.push("Sistema Venoso Profundo:");
    veiasProfundas.forEach(v => {
      linhas.push(`- ${v}: ${profundas[l][v]}`);
    });
    linhas.push("");
    linhas.push("Sistema Venoso Superficial:");
    veiasSuperficiais.forEach(v => {
      let extra = "";
      if (v === "JSF" && jsfDiametro && jsfDiametro[l]) extra = ` (diâmetro: ${jsfDiametro[l]} mm)`;
      if (v === "JSP" && jspDiametro && jspDiametro[l]) extra = ` (diâmetro: ${jspDiametro[l]} mm)`;
      linhas.push(`- ${v}: ${superficiais[l][v]}${extra}`);
      if (v === "Safena Magna" && superficiais[l][v] === "pérvia e incompetente") {
        linhas.push(`  > Diâmetro - Coxa: ${magna[l].coxa} mm`);
        linhas.push(`  > Diâmetro - Perna: ${magna[l].perna} mm`);
        linhas.push(`  > Diâmetro - Tornozelo: ${magna[l].tornozelo} mm`);
        const ini = magna[l].inicio;
        const fim = magna[l].fim;
        const ini_val = magna[l].inicio_valor;
        const fim_val = magna[l].fim_valor;
        const ini_fmt = ini && ini.startsWith("cm_") && ini_val ? `${ini_val} ${ini.replace("cm_", "cm ")}` : ini;
        const fim_fmt = fim && fim.startsWith("cm_") && fim_val ? `${fim_val} ${fim.replace("cm_", "cm ")}` : fim;
        linhas.push(`  > Segmento insuficiente: de ${ini_fmt || ''} até ${fim_fmt || ''}`);
      }
      if (v === "Safena Parva" && superficiais[l][v] === "pérvia e incompetente") {
        linhas.push(`  > Diâmetro Proximal: ${parva[l].proximal} mm`);
        linhas.push(`  > Diâmetro Distal: ${parva[l].distal} mm`);
        const ini = parva[l].inicio;
        const fim = parva[l].fim;
        const ini_val = parva[l].inicio_valor;
        const fim_val = parva[l].fim_valor;
        const ini_fmt = ini && ini.startsWith("cm_") && ini_val ? `${ini_val} ${ini.replace("cm_", "cm ")}` : ini;
        const fim_fmt = fim && fim.startsWith("cm_") && fim_val ? `${fim_val} ${fim.replace("cm_", "cm ")}` : fim;
        linhas.push(`  > Segmento insuficiente: de ${ini_fmt || ''} até ${fim_fmt || ''}`);
      }
    });
    linhas.push("");
    linhas.push("Veias Perfurantes:");
    if (perfurantes[l] && perfurantes[l].status) {
      linhas.push(`- Status: ${perfurantes[l].status}`);
      if (perfurantes[l].segmento && perfurantes[l].valor) {
        linhas.push(`- Localização: ${perfurantes[l].valor} ${perfurantes[l].segmento}`);
      }
    } else {
      linhas.push("- Não especificado");
    }
    linhas.push("");
    linhas.push("CONCLUSÃO:");
    linhas.push(gerarConclusaoPorLado({ profundas: profundas[l], superficiais: superficiais[l], magna: magna[l], parva: parva[l], perfurantes: perfurantes[l], jsfDiametro: jsfDiametro[l], jspDiametro: jspDiametro[l] }));
    if (observacoes && observacoes[l]) {
      linhas.push("");
      linhas.push("OBSERVAÇÕES:");
      linhas.push(observacoes[l]);
    }
    return linhas.join("\n");
  });
  return blocos.join("\n\n" + "=".repeat(80) + "\n\n");
}

