          style={{ ...buttonStyle, background: '#0eb8d0', color: '#fff', marginTop: 18, minWidth: 180 }}
          onClick={() => {
            const laudo = montarLaudo({ nome, data, lado, profundas, superficiais, magna, parva, jsfDiametro, jspDiametro, observacoes, perfurantes });
            setLaudoTexto(laudo);
          }}
        >Visualizar Laudo</button>
      </div>
      {/* Exibição do laudo ao lado (lado único) ou embaixo (ambos) */}
      {laudoTexto && (
        <div style={{
          flex: 1,
          minWidth: 320,
          maxWidth: 540,
          marginLeft: 32,
          marginTop: 0,
          background: "#fff",
          color: "#222",
          borderRadius: 12,
          padding: 28,
          boxShadow: "0 8px 32px #00e0ff33",
          fontFamily: "monospace",
          fontSize: 15,
          lineHeight: 1.6,
          whiteSpace: "pre-wrap"
        }}>
          <div style={{ display: "flex", justifyContent: "flex-end", gap: 12, marginBottom: 10 }}>
            <button style={{ ...buttonStyle, background: "#0eb8d0", color: "#fff" }} onClick={() => {
              // Salvar TXT
              const blob = new Blob([laudoTexto], { type: "text/plain;charset=utf-8" });
              saveAs(blob, `Laudo_${nome}_${data}.txt`);
            }}>Salvar TXT</button>
            <button style={{ ...buttonStyle, background: "#0eb8d0", color: "#fff" }} onClick={() => {
              // Salvar PDF
              const doc = new jsPDF();
              const lines = laudoTexto.split("\n");
              let y = 15;
              doc.setFont("courier", "normal");
              lines.forEach(line => {
                if (line.startsWith("PACIENTE:") || line.startsWith("DOPPLER") || line.startsWith("Sistema") || line.startsWith("CONCLUSÃO")) {
                  doc.setFont(undefined, "bold");
                } else {
                  doc.setFont(undefined, "normal");
                }
                doc.text(line, 10, y);
                y += 8;
                if (y > 280) { doc.addPage(); y = 15; }
              });
              doc.save(`Laudo_${nome}_${data}.pdf`);
            }}>Salvar PDF</button>
          </div>
          {laudoTexto}
        </div>
      )}
      <style>{`
        @keyframes logoGlow {
          0% {
            filter: drop-shadow(0 10px 32px #00e0ff90);
          }
          100% {
            filter: drop-shadow(0 18px 48px #00e0ffc0);
          }
        }
      `}</style>
    </div>
  );
}

const inputStyle = {
  background: "#f7fbff",
  border: "1.5px solid #0eb8d0",
  borderRadius: 7,
  padding: "8px 12px",
  fontSize: 15,
  color: "#222",
  outline: "none",
  fontFamily: "inherit",
  fontWeight: 500,
  width: 180,
  minWidth: 0
};

const buttonStyle = {
  background: "#0eb8d0",
  color: "#fff",
  fontWeight: 600,
  fontSize: 16,
  padding: "10px 22px",
  border: "none",
  borderRadius: 8,
  cursor: "pointer",
  letterSpacing: 0.5,
  boxShadow: "0 2px 10px #00e0ff30",
  transition: ".2s",
  marginLeft: 8,
  minWidth: 140
};

export default MMIIVenoso;
