import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { PDFDocument, rgb, StandardFonts } from "pdf-lib";
import { saveAs } from "file-saver";
import { FiArrowLeft, FiEye, FiEdit, FiPrinter, FiX, FiTrash2 } from "react-icons/fi";

// Helpers para exames salvos (localStorage)
const STORAGE_KEY = "examesMMIIVenoso";
function getExamesSalvos() {
  return JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
}
function salvarExame(exame) {
  const todos = getExamesSalvos();
  todos.push(exame);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
}
function atualizarExames(novos) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(novos));
}

export default function MMIIVenoso() {
  const navigate = useNavigate();

  // Campos do exame
  const [nome, setNome] = useState("");
  const [data, setData] = useState("");
  const [lado, setLado] = useState("");
  const [dados, setDados] = useState({});
  const [laudo, setLaudo] = useState("");
  const [mostrarExamesSalvos, setMostrarExamesSalvos] = useState(false);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [exameEmEdicao, setExameEmEdicao] = useState(null);
  const [exameVisualizando, setExameVisualizando] = useState(null);

  // Estados para configuração do PDF
  const [mostrarConfiguracao, setMostrarConfiguracao] = useState(false);
  const [configClinica, setConfigClinica] = useState({
    nomeClinica: localStorage.getItem("nomeClinica") || "",
    enderecoClinica: localStorage.getItem("enderecoClinica") || "",
    telefoneClinica: localStorage.getItem("telefoneClinica") || "",
    emailClinica: localStorage.getItem("emailClinica") || "",
    logoClinica: localStorage.getItem("logoClinica") || "",
  });
  const [configMedico, setConfigMedico] = useState({
    nomeMedico: localStorage.getItem("nomeMedico") || "",
    especialidade: localStorage.getItem("especialidadeLaudo") || "",
    crm: localStorage.getItem("crm") || "",
    assinaturaMedico: localStorage.getItem("assinaturaMedico") || "",
  });

  // Campos das veias
  const veiasProfundas = [
    "Veia Femoral Comum", "Veia Femoral Superficial", "Veia Femoral Profunda",
    "Veia Poplítea", "Veias Tibiais posteriores", "Veias Tibiais anteriores",
    "Veias Gastrocnêmicas", "Veias Soleares"
  ];
  const veiasSuperficiais = ["JSF", "Safena Magna", "JSP", "Safena Parva"];
  const ladosPossiveis = ["Direito", "Esquerdo", "Ambos"];

  // Estado dos campos das veias
  function initCampos() {
    return {
      profundo: Object.fromEntries(veiasProfundas.map(v => [v, "pérvia e competente"])),
      superficial: Object.fromEntries(veiasSuperficiais.map(v => [v, "pérvia e competente"])),
      safena: {
        "Safena Magna": {
          coxa: "", perna: "", tornozelo: "",
          inicio: "", inicio_valor: "", fim: "", fim_valor: ""
        },
        "Safena Parva": {
          proximal: "", distal: "",
          inicio: "", inicio_valor: "", fim: "", fim_valor: ""
        }
      },
      perfurante: "competentes",
      local_perfurante_valor: "",
      local_perfurante_tipo: "",
      obs: "",
    };
  }
  const [camposD, setCamposD] = useState(initCampos());
  const [camposE, setCamposE] = useState(initCampos());

  function handleLadoChange(val) {
    setLado(val);
    if (val === "Direito") setDados({ "Direito": { ...camposD } });
    else if (val === "Esquerdo") setDados({ "Esquerdo": { ...camposE } });
    else setDados({ "Direito": { ...camposD }, "Esquerdo": { ...camposE } });
  }
  function atualizaCampos(side, novos) {
    if (side === "Direito") { setCamposD(novos); setDados({ ...dados, Direito: novos }); }
    else { setCamposE(novos); setDados({ ...dados, Esquerdo: novos }); }
  }

  // Gerador de Laudo
  function gerarLaudo() {
    if (!nome || !data || !lado) {
      alert("Preencha nome, data e lado!");
      return;
    }
    let laudos = {};
    const sides = (lado === "Ambos") ? ["Direito", "Esquerdo"] : [lado];

    console.log("Gerando laudos para lados:", sides);
    console.log("Lado selecionado:", lado);

    sides.forEach(side => {
      const c = side === "Direito" ? camposD : camposE;
      console.log(`Processando lado: ${side}`, c);

      let txt = "";
      txt += `PACIENTE: ${nome}\n`;
      txt += `DATA: ${data}\n`;
      txt += `DOPPLER VENOSO DE MEMBRO INFERIOR ${side.toUpperCase()}\n\n`;
      txt += "Sistema Venoso Profundo:\n";
      veiasProfundas.forEach(v =>
        txt += `- ${v}: ${c.profundo[v]}\n`
      );
      txt += "\nSistema Venoso Superficial:\n";
      veiasSuperficiais.forEach(v => {
        txt += `- ${v}: ${c.superficial[v]}\n`;
        if (["Safena Magna", "Safena Parva"].includes(v) && c.superficial[v] === "pérvia e insuficiente") {
          const s = c.safena[v];
          if (v === "Safena Magna") {
            txt += `  > Diâmetro - Coxa: ${s.coxa} mm\n`;
            txt += `  > Diâmetro - Perna: ${s.perna} mm\n`;
            txt += `  > Diâmetro - Tornozelo: ${s.tornozelo} mm\n`;
          } else {
            txt += `  > Diâmetro Proximal: ${s.proximal} mm\n`;
            txt += `  > Diâmetro Distal: ${s.distal} mm\n`;
          }
          txt += `  > Segmento insuficiente: de ${s.inicio_valor} ${s.inicio} até ${s.fim_valor} ${s.fim}\n`;
        }
      });
      txt += `\nVeias Perfurantes: ${c.perfurante}\n`;
      if (c.perfurante === "incompetentes" && (c.local_perfurante_valor || c.local_perfurante_tipo)) {
        txt += `  > Localização: ${c.local_perfurante_valor} ${c.local_perfurante_tipo}\n`;
      }
      if (c.obs && c.obs.trim()) {
        txt += `\nObservações (${side}):\n${c.obs}\n`;
      }
      txt += `\nCONCLUSÃO:\n`;
      txt += gerarConclusaoPorSide(c, side);
      txt += `\n\n`;
      laudos[side] = txt;
    });

    console.log("Laudos gerados:", Object.keys(laudos));
    setLaudo(Object.values(laudos).join("\n"));
    return laudos;
  }

  function gerarConclusaoPorSide(c, side) {
    const profundos = c.profundo;
    const superficiais = c.superficial;
    const segmentos = c.safena;
    const perfurante = c.perfurante;
    const local_perf = `${c.local_perfurante_valor} ${c.local_perfurante_tipo}`;
    let conclusoes = [];
    if (Object.values(profundos).some(v => v.includes("não compressível"))) {
      conclusoes.push("Trombose venosa profunda");
    }
    if (Object.values(profundos).some(v => v.includes("semi compressível"))) {
      conclusoes.push("Sinais de recanalização parcial do sistema venoso profundo");
    }
    if (Object.values(profundos).some(v => v.includes("incompetente"))) {
      conclusoes.push("Insuficiência de sistema venoso profundo");
    }
    ["Safena Magna", "Safena Parva"].forEach(veia => {
      if (superficiais[veia] === "pérvia e insuficiente") {
        const ini = segmentos[veia]["inicio"];
        const fim = segmentos[veia]["fim"];
        const ini_val = segmentos[veia]["inicio_valor"];
        const fim_val = segmentos[veia]["fim_valor"];
        const ini_fmt = (ini && ini.includes("cm") && ini_val) ? `${ini_val} ${ini}` : ini;
        const fim_fmt = (fim && fim.includes("cm") && fim_val) ? `${fim_val} ${fim}` : fim;
        if ((ini_fmt && ini_fmt.includes("JSF") && fim_fmt && fim_fmt.includes("tornozelo")) ||
            (ini_fmt && ini_fmt.includes("JSP") && fim_fmt && fim_fmt.includes("tornozelo"))) {
          conclusoes.push(`Insuficiência total da ${veia.toLowerCase()}`);
        } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("tornozelo")) {
          conclusoes.push(`Insuficiência parcial da ${veia.toLowerCase()}`);
        } else if (ini_fmt && ini_fmt.includes("joelho") && fim_fmt && fim_fmt.includes("joelho")) {
          conclusoes.push(`Insuficiência segmentar da ${veia.toLowerCase()}`);
        }
      }
    });
    if (superficiais["Safena Magna"] === "não compressível e sem fluxo (trombose)") {
      conclusoes.push("Tromboflebite da safena magna");
    }
    if (superficiais["Safena Parva"] === "não compressível e sem fluxo (trombose)") {
      conclusoes.push("Tromboflebite da safena parva");
    }
    if (superficiais["JSF"] === "pérvia e insuficiente") {
      conclusoes.push("Incompetência da junção safeno-femoral (JSF)");
    }
    if (superficiais["JSP"] === "pérvia e insuficiente") {
      conclusoes.push("Incompetência da junção safeno-poplítea (JSP)");
    }
    if (perfurante === "incompetentes") {
      if (local_perf.trim()) conclusoes.push(`Insuficiência de veias perfurantes (${local_perf})`);
      else conclusoes.push("Insuficiência de veias perfurantes");
    }
    if (!conclusoes.length) return "- Ausência de refluxo venoso nos territórios estudados.";
    return "- " + conclusoes.join("\n- ");
  }

  // PDF
  async function gerarPDFExame({ nome, data, lados, laudosPorLado }) {
    console.log("=== INICIANDO GERAÇÃO DE PDF ===");
    const doc = await PDFDocument.create();
    const font = await doc.embedFont(StandardFonts.Helvetica);
    const fontBold = await doc.embedFont(StandardFonts.HelveticaBold);

    // Carregar logo se existir
    let logoImage = null;
    const logoClinica = localStorage.getItem("logoClinica");
    if (logoClinica) {
      try {
        logoImage = await doc.embedPng(logoClinica);
      } catch (error) {
        console.log("Erro ao carregar logo:", error);
      }
    }

    // Carregar assinatura se existir
    let assinaturaImage = null;
    const assinaturaMedico = localStorage.getItem("assinaturaMedico");
    if (assinaturaMedico && assinaturaMedico.length > 100) {
      try {
        assinaturaImage = await doc.embedPng(assinaturaMedico);
      } catch (error) {
        try {
          assinaturaImage = await doc.embedJpg(assinaturaMedico);
        } catch (error2) {
          console.log("Erro ao carregar assinatura:", error2);
        }
      }
    }

    // Obter dados do médico e da clínica
    const nomeMedico = localStorage.getItem("nomeMedico") || "";
    const especialidade = localStorage.getItem("especialidadeLaudo") || "";
    const crm = localStorage.getItem("crm") || "";
    const nomeClinica = localStorage.getItem("nomeClinica") || "";
    const enderecoClinica = localStorage.getItem("enderecoClinica") || "";
    const telefoneClinica = localStorage.getItem("telefoneClinica") || "";
    const emailClinica = localStorage.getItem("emailClinica") || "";

    // Dimensões da página A4
    const PAGE_WIDTH = 595;
    const PAGE_HEIGHT = 842;
    const MARGEM_SUP = 50; // Margem superior para cabeçalho
    const MARGEM_INF = 80; // Margem inferior para rodapé
    const MARGEM_LAT = 40; // Margem lateral
    const CABECALHO_HEIGHT = 80; // Altura do cabeçalho
    const RODAPE_HEIGHT = 60; // Altura do rodapé

    lados.forEach(ladoX => {
      const laudo = laudosPorLado[ladoX];
      const page = doc.addPage([PAGE_WIDTH, PAGE_HEIGHT]);
      const lineHeight = 16;
      const larguraUtil = PAGE_WIDTH - 2 * MARGEM_LAT;
      let y = PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT; // Começar após o cabeçalho

      // === CABEÇALHO FIXO ===
      // Logo à esquerda
      if (logoImage) {
        const logoWidth = 100;
        const logoHeight = (logoImage.height * logoWidth) / logoImage.width;
        const logoY = PAGE_HEIGHT - MARGEM_SUP - (logoHeight / 2);
        page.drawImage(logoImage, {
          x: MARGEM_LAT,
          y: logoY,
          width: logoWidth,
          height: logoHeight,
        });
      }

      // Dados da clínica à direita
      const dadosClinicaX = PAGE_WIDTH - MARGEM_LAT - 200;
      const dadosClinicaY = PAGE_HEIGHT - MARGEM_SUP - 20;

      if (nomeClinica) {
        page.drawText(nomeClinica, {
          x: dadosClinicaX,
          y: dadosClinicaY,
          size: 14,
          font: fontBold,
          color: rgb(0, 0, 0),
        });
      }

      if (enderecoClinica) {
        page.drawText(enderecoClinica, {
          x: dadosClinicaX,
          y: dadosClinicaY - 16,
          size: 10,
          font: font,
          color: rgb(0, 0, 0),
        });
      }

      if (telefoneClinica) {
        page.drawText(`Tel: ${telefoneClinica}`, {
          x: dadosClinicaX,
          y: dadosClinicaY - 32,
          size: 10,
          font: font,
          color: rgb(0, 0, 0),
        });
      }

      if (emailClinica) {
        page.drawText(`Email: ${emailClinica}`, {
          x: dadosClinicaX,
          y: dadosClinicaY - 48,
          size: 10,
          font: font,
          color: rgb(0, 0, 0),
        });
      }

      // Linha separadora do cabeçalho
      // page.drawLine({
      //   start: { x: MARGEM_LAT, y: PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT - 10 },
      //   end: { x: PAGE_WIDTH - MARGEM_LAT, y: PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT - 10 },
      //   thickness: 1,
      //   color: rgb(0, 0, 0),
      // });

      // === CONTEÚDO DO LAUDO ===
      const linhas = laudo.split('\n');
      let yAtual = y;

      linhas.forEach(linha => {
        if (linha.trim() === '') {
          yAtual -= lineHeight / 2;
          return;
        }

        // Verificar se precisa de nova página
        if (yAtual < MARGEM_INF + RODAPE_HEIGHT + lineHeight) {
          // Adicionar rodapé na página atual
          adicionarRodape(page, assinaturaImage, nomeMedico, especialidade, crm, PAGE_WIDTH, PAGE_HEIGHT, MARGEM_LAT, MARGEM_INF, RODAPE_HEIGHT, font, fontBold);

          // Nova página
          const novaPage = doc.addPage([PAGE_WIDTH, PAGE_HEIGHT]);

          // Adicionar cabeçalho na nova página
          adicionarCabecalho(novaPage, logoImage, nomeClinica, enderecoClinica, telefoneClinica, emailClinica, PAGE_WIDTH, PAGE_HEIGHT, MARGEM_LAT, MARGEM_SUP, CABECALHO_HEIGHT, font, fontBold);

          // Atualizar referências
          page = novaPage;
          yAtual = PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT;
        }

        // Aplicar estilos especiais
        let fonteAtual = font;
        let tamanhoAtual = 11;
        let corAtual = rgb(0, 0, 0);

        // Nome do paciente em negrito
        if (linha.startsWith('PACIENTE:')) {
          fonteAtual = fontBold;
          tamanhoAtual = 13;
        }
        // Títulos de seção em negrito
        else if (linha.includes('Sistema Venoso') || linha.includes('Veias Perfurantes') || 
                 linha.includes('Observações') || linha.includes('DOPPLER VENOSO')) {
          fonteAtual = fontBold;
          tamanhoAtual = 12;
        }
        // Conclusão em negrito
        else if (linha.startsWith('CONCLUSÃO:') || linha.startsWith('- ')) {
          fonteAtual = fontBold;
          tamanhoAtual = 11;
        }

        // Quebrar linhas longas
        const palavras = linha.split(' ');
        let linhaAtual = '';
        let linhasQuebradas = [];

        palavras.forEach(palavra => {
          const testeLinha = linhaAtual + (linhaAtual ? ' ' : '') + palavra;
          const larguraTexto = fonteAtual.widthOfTextAtSize(testeLinha, tamanhoAtual);

          if (larguraTexto > larguraUtil) {
            if (linhaAtual) {
              linhasQuebradas.push(linhaAtual);
              linhaAtual = palavra;
            } else {
              linhasQuebradas.push(palavra);
            }
          } else {
            linhaAtual = testeLinha;
          }
        });

        if (linhaAtual) {
          linhasQuebradas.push(linhaAtual);
        }

        // Desenhar cada linha
        linhasQuebradas.forEach(texto => {
          page.drawText(texto, {
            x: MARGEM_LAT,
            y: yAtual,
            size: tamanhoAtual,
            font: fonteAtual,
            color: corAtual,
            maxWidth: larguraUtil,
          });

          yAtual -= lineHeight;
        });
      });

      // Adicionar rodapé na última página
      adicionarRodape(page, assinaturaImage, nomeMedico, especialidade, crm, PAGE_WIDTH, PAGE_HEIGHT, MARGEM_LAT, MARGEM_INF, RODAPE_HEIGHT, font, fontBold);
    });

