    function adicionarCabecalho(page, logoImage, nomeClinica, enderecoClinica, telefoneClinica, emailClinica, PAGE_WIDTH, PAGE_HEIGHT, MARGEM_LAT, MARGEM_SUP, CABECALHO_HEIGHT, font, fontBold) {
