        });
      }

      // Linha separadora - REMOVIDA
      // page.drawLine({
      //   start: { x: MARGEM_LAT, y: PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT - 10 },
      //   end: { x: PAGE_WIDTH - MARGEM_LAT, y: PAGE_HEIGHT - MARGEM_SUP - CABECALHO_HEIGHT - 10 },
      //   thickness: 1,
      //   color: rgb(0, 0, 0),
      // });
    }

    // Função auxiliar para adicionar rodapé
    function adicionarRodape(page, assinaturaImage, nomeMedico, especialidade, crm, PAGE_WIDTH, PAGE_HEIGHT, MARGEM_LAT, MARGEM_INF, RODAPE_HEIGHT, font, fontBold) {
      const yRodape = MARGEM_INF + 40;

      // Linha separadora do rodapé - REMOVIDA
      // page.drawLine({
      //   start: { x: MARGEM_LAT, y: MARGEM_INF + RODAPE_HEIGHT },
      //   end: { x: PAGE_WIDTH - MARGEM_LAT, y: MARGEM_INF + RODAPE_HEIGHT },
      //   thickness: 1,
      //   color: rgb(0, 0, 0),
      // });

      // Assinatura
      if (assinaturaImage) {
        const assinaturaWidth = 60;
        const assinaturaHeight = (assinaturaImage.height * assinaturaWidth) / assinaturaImage.width;
        page.drawImage(assinaturaImage, {
          x: PAGE_WIDTH - MARGEM_LAT - assinaturaWidth,
          y: yRodape + 15,
          width: assinaturaWidth,
          height: assinaturaHeight,
        });
      }

      // Linha da assinatura - REMOVIDA
      // page.drawLine({
      //   start: { x: PAGE_WIDTH - MARGEM_LAT - 120, y: yRodape + 10 },
      //   end: { x: PAGE_WIDTH - MARGEM_LAT, y: yRodape + 10 },
      //   thickness: 1,
      //   color: rgb(0, 0, 0),
      // });

      // Dados do médico à direita
      const medicoX = PAGE_WIDTH - MARGEM_LAT - 120;

      if (nomeMedico) {
        page.drawText(nomeMedico, {
          x: medicoX,
          y: yRodape,
          size: 11,
          font: fontBold,
          color: rgb(0, 0, 0),
        });
