      if (especialidade) {
        page.drawText(especialidade, {
          x: medicoX,
          y: yRodape - 14,
          size: 10,
          font: font,
          color: rgb(0, 0, 0),
        });
      }

      if (crm) {
        page.drawText(`CRM: ${crm}`, {
          x: medicoX,
          y: yRodape - 28,
          size: 10,
          font: font,
          color: rgb(0, 0, 0),
        });
      }
    }

    // Salvar o PDF
    const pdfBytes = await doc.save();
    const blob = new Blob([pdfBytes], { type: 'application/pdf' });
    saveAs(blob, `Laudo_MMII_Venoso_${nome}_${data}.pdf`);
  }

  // Funções auxiliares
  function salvarExameAtual() {
    if (!nome || !data || !lado || !laudo) {
      alert("Gere um laudo primeiro!");
      return;
    }
    const exame = { nome, data, lado, laudo, timestamp: Date.now() };
    salvarExame(exame);
    alert("Exame salvo!");
    limparFormulario();
  }

  function carregarExameParaEdicao(exame) {
    setNome(exame.nome);
    setData(exame.data);
    setLado(exame.lado);
    setLaudo(exame.laudo);
    setExameEmEdicao(exame);
    setMostrarFormulario(true);
    setMostrarExamesSalvos(false);
  }

  function atualizarExame() {
    if (!exameEmEdicao) return;
    const exames = getExamesSalvos();
    const index = exames.findIndex(e => e.timestamp === exameEmEdicao.timestamp);
    if (index !== -1) {
      exames[index] = { nome, data, lado, laudo, timestamp: exameEmEdicao.timestamp };
      atualizarExames(exames);
      alert("Exame atualizado!");
      setExameEmEdicao(null);
      setMostrarFormulario(false);
    }
  }

  function excluirExame(exame) {
    if (confirm("Tem certeza que deseja excluir este exame?")) {
      const exames = getExamesSalvos();
      const novos = exames.filter(e => e.timestamp !== exame.timestamp);
      atualizarExames(novos);
      alert("Exame excluído!");
    }
  }

  function limparFormulario() {
    setNome("");
    setData("");
    setLado("");
    setLaudo("");
    setCamposD(initCampos());
    setCamposE(initCampos());
    setDados({});
    setExameEmEdicao(null);
    setExameVisualizando(null);
  }

  // Funções para configuração do PDF
  function handleLogoChange(event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const logoData = e.target.result;
        setConfigClinica(prev => ({ ...prev, logoClinica: logoData }));
        localStorage.setItem("logoClinica", logoData);
      };
      reader.readAsDataURL(file);
    }
  }

  function handleAssinaturaChange(event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const assinaturaData = e.target.result;
        setConfigMedico(prev => ({ ...prev, assinaturaMedico: assinaturaData }));
        localStorage.setItem("assinaturaMedico", assinaturaData);
      };
      reader.readAsDataURL(file);
    }
  }

  function salvarConfiguracaoClinica() {
    localStorage.setItem("nomeClinica", configClinica.nomeClinica);
    localStorage.setItem("enderecoClinica", configClinica.enderecoClinica);
    localStorage.setItem("telefoneClinica", configClinica.telefoneClinica);
    localStorage.setItem("emailClinica", configClinica.emailClinica);
    alert("Configuração da clínica salva com sucesso!");
  }

  function salvarConfiguracaoMedico() {
    localStorage.setItem("nomeMedico", configMedico.nomeMedico);
    localStorage.setItem("especialidadeLaudo", configMedico.especialidade);
    localStorage.setItem("crm", configMedico.crm);
    alert("Configuração do médico salva com sucesso!");
  }

