                                atualizaCampos("Esquerdo", novos);
                              }}
                              placeholder="Tipo (ex: cm, mm)"
                              style={{
                                padding: "6px 10px",
                                border: "1px solid #0eb8d0",
                                borderRadius: 4,
                                background: "#1c2740",
                                color: "#fff",
                                fontSize: 12,
                                outline: "none",
                                width: 100
                              }}
                            />
                          </>
                        )}
                      </div>
              </div>

              {/* Observações */}
                    <div>
                      <h4 style={{
                        fontSize: 16,
                        fontWeight: 600,
                        color: "#abfaff",
                        marginBottom: 12
                      }}>Observações:</h4>
                <textarea
                        value={camposE.obs}
                  onChange={e => {
                          const novos = { ...camposE };
                    novos.obs = e.target.value;
                          atualizaCampos("Esquerdo", novos);
                  }}
                        placeholder="Observações adicionais..."
                  style={{ 
                    width: "100%", 
                          height: 80,
                          padding: "12px",
                          border: "1px solid #0eb8d0",
                    borderRadius: 6, 
                          background: "#1c2740",
                          color: "#fff",
                          fontSize: 12,
                          outline: "none",
                    resize: "vertical"
                  }}
                />
                    </div>
              </div>
            </div>
          )}
            </div>

            {exameEmEdicao && (
              <div style={{ display: "flex", gap: 12 }}>
            <button
                  onClick={atualizarExame}
              style={{
                    flex: 1,
                    background: "#0eb8d0",
                border: "none",
                    borderRadius: 10,
                    padding: "12px 20px",
                    color: "#fff",
                fontWeight: 600,
                    fontSize: 15,
                    cursor: "pointer",
                    transition: ".2s",
                    letterSpacing: 0.5
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.background = "#0ca8b8";
                    e.target.style.transform = "translateY(-2px)";
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.background = "#0eb8d0";
                    e.target.style.transform = "translateY(0)";
                  }}
                >
                  Atualizar Exame
            </button>
              <button
                  onClick={() => setExameEmEdicao(null)}
                style={{
                    background: "#6c757d",
                    border: "none",
                    borderRadius: 10,
                    padding: "12px 20px",
                  color: "#fff",
                    fontWeight: 600,
                    fontSize: 15,
                    cursor: "pointer",
                    transition: ".2s",
                    letterSpacing: 0.5
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.background = "#5a6268";
                    e.target.style.transform = "translateY(-2px)";
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.background = "#6c757d";
                    e.target.style.transform = "translateY(0)";
                  }}
                >
                  Cancelar
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Modal de Visualização */}
      {exameVisualizando && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: "rgba(0, 0, 0, 0.8)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: 20,
          zIndex: 1000
        }}>
          <div style={{
            background: "#121e38",
            borderRadius: 15,
            maxWidth: 800,
            width: "100%",
            maxHeight: "80vh",
            overflow: "hidden",
            border: "1px solid #0eb8d0"
          }}>
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              padding: 24,
              borderBottom: "1px solid #0eb8d0"
            }}>
              <h3 style={{
                fontSize: 20,
                fontWeight: 600,
                color: "#fff",
                margin: 0
              }}>
                {exameVisualizando.nome} - {exameVisualizando.data}
              </h3>
              <button
                onClick={() => setExameVisualizando(null)}
                style={{
                  background: "#dc3545",
                  border: "none",
                  borderRadius: 8,
                  padding: 8,
                  color: "#fff",
                  cursor: "pointer",
                  transition: ".2s"
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = "#c82333";
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = "#dc3545";
                }}
              >
                <FiX size={20} />
              </button>
            </div>
            <div style={{
              padding: 24,
              overflowY: "auto",
              maxHeight: "60vh"
            }}>
              <pre style={{
                whiteSpace: "pre-wrap",
                fontSize: 13,
                fontFamily: "monospace",
                color: "#fff",
                margin: 0,
                lineHeight: 1.5
              }}>{exameVisualizando.laudo}</pre>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
