                          onChange={e => {
                            const novos = { ...camposD };
                            novos.obs = e.target.value;
                            atualizaCampos("Direito", novos);
                          }}
                          placeholder="Observações adicionais..."
                          style={{
                            width: "100%",
                            height: 80,
                            padding: "12px",
                            border: "1px solid #0eb8d0",
                            borderRadius: 6,
                            background: "#1c2740",
                            color: "#fff",
                            fontSize: 12,
                            outline: "none",
                            resize: "vertical"
                          }}
                        />
                      </div>
                    </div>

                    {/* Lado Esquerdo */}
                    <div style={{
                      background: "#1c2740",
                      borderRadius: 10,
                      padding: 16,
                      border: "1px solid #0eb8d0"
                    }}>
                      <h3 style={{
                        fontSize: 18,
                        fontWeight: 600,
                        color: "#0eb8d0",
                        marginBottom: 16,
                        textAlign: "center"
                      }}>Lado Esquerdo</h3>

                      {/* Sistema Venoso Profundo */}
                      <div style={{ marginBottom: 20 }}>
                        <h4 style={{
                          fontSize: 16,
                          fontWeight: 600,
                          color: "#abfaff",
                          marginBottom: 12
                        }}>Sistema Venoso Profundo:</h4>
                        {veiasProfundas.map(veia => (
                          <div key={veia} style={{
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                            marginBottom: 8,
                            padding: "8px 12px",
                            background: "#121e38",
                            borderRadius: 6
                          }}>
                            <label style={{
                              fontSize: 13,
                              color: "#fff",
                              flex: 1
                            }}>{veia}:</label>
                            <select
                              value={camposE.profundo[veia]}
                              onChange={e => {
                                const novos = { ...camposE };
                                novos.profundo[veia] = e.target.value;
                                atualizaCampos("Esquerdo", novos);
                              }}
                              style={{
                                padding: "6px 10px",
                                border: "1px solid #0eb8d0",
                                borderRadius: 4,
                                background: "#1c2740",
                                color: "#fff",
                                fontSize: 12,
                                outline: "none",
                                minWidth: 200
                              }}
                            >
                              <option value="pérvia e competente">pérvia e competente</option>
                              <option value="pérvia e incompetente">pérvia e incompetente</option>
                              <option value="não compressível e sem fluxo (trombose)">não compressível e sem fluxo (trombose)</option>
                              <option value="semi compressível">semi compressível</option>
                            </select>
                          </div>
                        ))}
                      </div>

                      {/* Sistema Venoso Superficial */}
                      <div style={{ marginBottom: 20 }}>
                        <h4 style={{
                          fontSize: 16,
                          fontWeight: 600,
                          color: "#abfaff",
                          marginBottom: 12
                        }}>Sistema Venoso Superficial:</h4>
                        {veiasSuperficiais.map(veia => (
                          <div key={veia} style={{
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                            marginBottom: 8,
                            padding: "8px 12px",
                            background: "#121e38",
                            borderRadius: 6
                          }}>
                            <label style={{
                              fontSize: 13,
                              color: "#fff",
                              flex: 1
                            }}>{veia}:</label>
                            <select
                              value={camposE.superficial[veia]}
                              onChange={e => {
                                const novos = { ...camposE };
                                novos.superficial[veia] = e.target.value;
                                atualizaCampos("Esquerdo", novos);
                              }}
                              style={{
                                padding: "6px 10px",
                                border: "1px solid #0eb8d0",
                                borderRadius: 4,
                                background: "#1c2740",
                                color: "#fff",
                                fontSize: 12,
                                outline: "none",
                                minWidth: 200
                              }}
                            >
                              <option value="pérvia e competente">pérvia e competente</option>
                              <option value="pérvia e insuficiente">pérvia e insuficiente</option>
                              <option value="não compressível e sem fluxo (trombose)">não compressível e sem fluxo (trombose)</option>
                            </select>
                          </div>
                        ))}
                      </div>

                      {/* Veias Perfurantes */}
                      <div style={{ marginBottom: 20 }}>
                        <h4 style={{
                          fontSize: 16,
                          fontWeight: 600,
                          color: "#abfaff",
                          marginBottom: 12
                        }}>Veias Perfurantes:</h4>
                        <div style={{
                          display: "flex",
                          gap: 12,
                          alignItems: "center",
                          padding: "8px 12px",
                          background: "#121e38",
                          borderRadius: 6
                        }}>
                          <select
                            value={camposE.perfurante}
                            onChange={e => {
                              const novos = { ...camposE };
                              novos.perfurante = e.target.value;
                              atualizaCampos("Esquerdo", novos);
                            }}
                            style={{
                              padding: "6px 10px",
                              border: "1px solid #0eb8d0",
                              borderRadius: 4,
                              background: "#1c2740",
                              color: "#fff",
                              fontSize: 12,
                              outline: "none"
                            }}
                          >
                            <option value="competentes">competentes</option>
                            <option value="incompetentes">incompetentes</option>
                          </select>
                          {camposE.perfurante === "incompetentes" && (
                            <>
                              <input
                                type="text"
                                value={camposE.local_perfurante_valor}
                                onChange={e => {
                                  const novos = { ...camposE };
                                  novos.local_perfurante_valor = e.target.value;
                                  atualizaCampos("Esquerdo", novos);
                                }}
                                placeholder="Valor"
                                style={{
                                  padding: "6px 10px",
                                  border: "1px solid #0eb8d0",
                                  borderRadius: 4,
                                  background: "#1c2740",
                                  color: "#fff",
                                  fontSize: 12,
                                  outline: "none",
                                  width: 80
                                }}
                              />
                              <input
                                type="text"
                                value={camposE.local_perfurante_tipo}
                                onChange={e => {
                                  const novos = { ...camposE };
                                  novos.local_perfurante_tipo = e.target.value;
                                  atualizaCampos("Esquerdo", novos);
                                }}
                                placeholder="Tipo (ex: cm, mm)"
                                style={{
                                  padding: "6px 10px",
                                  border: "1px solid #0eb8d0",
                                  borderRadius: 4,
                                  background: "#1c2740",
                                  color: "#fff",
                                  fontSize: 12,
                                  outline: "none",
                                  width: 100
                                }}
                              />
                            </>
                          )}
                        </div>
                      </div>

                      {/* Observações */}
                      <div>
                        <h4 style={{
                          fontSize: 16,
                          fontWeight: 600,
                          color: "#abfaff",
                          marginBottom: 12
                        }}>Observações:</h4>
                        <textarea
                          value={camposE.obs}
                          onChange={e => {
                            const novos = { ...camposE };
                            novos.obs = e.target.value;
                            atualizaCampos("Esquerdo", novos);
                          }}
                          placeholder="Observações adicionais..."
                          style={{
                            width: "100%",
                            height: 80,
                            padding: "12px",
                            border: "1px solid #0eb8d0",
                            borderRadius: 6,
                            background: "#1c2740",
                            color: "#fff",
                            fontSize: 12,
                            outline: "none",
                            resize: "vertical"
                          }}
                        />
                      </div>
                    </div>
                  </div>
                )}

                {exameEmEdicao && (
                  <div style={{ display: "flex", gap: 12 }}>
                    <button
                      onClick={atualizarExame}
                      style={{
                        flex: 1,
                        background: "#0eb8d0",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 20px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 15,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#0ca8b8";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#0eb8d0";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Atualizar Exame
                    </button>
                    <button
                      onClick={() => setExameEmEdicao(null)}
                      style={{
                        background: "#6c757d",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 20px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 15,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#5a6268";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#6c757d";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Cancelar
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Laudo Gerado */}
            <div style={{
              background: "#121e38",
              borderRadius: 15,
              padding: 24,
              border: "1px solid #0eb8d0"
            }}>
              <div style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 20
              }}>
                <h2 style={{
                  fontSize: 24,
                  fontWeight: 600,
                  color: "#fff",
                  margin: 0
                }}>Laudo Gerado</h2>
                {laudo && (
                  <div style={{ display: "flex", gap: 8 }}>
                    <button
                      onClick={() => {
                        const laudos = gerarLaudo();
                        if (laudos) {
                          const lados = Object.keys(laudos);
                          gerarPDFExame({ nome, data, lados, laudosPorLado: laudos });
                        }
                      }}
                      style={{
                        background: "#dc3545",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 16px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 14,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5,
                        display: "flex",
                        alignItems: "center",
                        gap: 8
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#c82333";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#dc3545";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      <FiPrinter size={16} />
                      Gerar PDF
                    </button>
                    <button
                      onClick={salvarExameAtual}
                      style={{
                        background: "#0eb8d0",
                        border: "none",
                        borderRadius: 10,
                        padding: "12px 16px",
                        color: "#fff",
                        fontWeight: 600,
                        fontSize: 14,
                        cursor: "pointer",
                        transition: ".2s",
                        letterSpacing: 0.5
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "#0ca8b8";
                        e.target.style.transform = "translateY(-2px)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "#0eb8d0";
                        e.target.style.transform = "translateY(0)";
                      }}
                    >
                      Salvar
                    </button>
                  </div>
                )}
              </div>

              {laudo ? (
                <div style={{
                  background: "#1c2740",
                  padding: 16,
                  borderRadius: 8,
                  border: "1px solid #0eb8d0",
                  maxHeight: "400px",
                  overflowY: "auto"
                }}>
                  <pre style={{
                    whiteSpace: "pre-wrap",
                    fontSize: 13,
                    fontFamily: "monospace",
                    color: "#fff",
                    margin: 0,
                    lineHeight: 1.5
                  }}>{laudo}</pre>
                </div>
              ) : (
                <div style={{
                  color: "#abfaff",
                  textAlign: "center",
                  padding: "40px 20px",
                  fontSize: 16
                }}>
                  Gere um laudo para visualizar aqui
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Modal de Visualização */}
      {exameVisualizando && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: "rgba(0, 0, 0, 0.8)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: 20,
          zIndex: 1000
        }}>
          <div style={{
            background: "#121e38",
            borderRadius: 15,
            maxWidth: 800,
            width: "100%",
            maxHeight: "80vh",
            overflow: "hidden",
            border: "1px solid #0eb8d0"
          }}>
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              padding: 24,
              borderBottom: "1px solid #0eb8d0"
            }}>
              <h3 style={{
                fontSize: 20,
                fontWeight: 600,
                color: "#fff",
                margin: 0
              }}>
                {exameVisualizando.nome} - {exameVisualizando.data}
              </h3>
              <button
                onClick={() => setExameVisualizando(null)}
                style={{
                  background: "#dc3545",
                  border: "none",
                  borderRadius: 8,
                  padding: 8,
                  color: "#fff",
                  cursor: "pointer",
                  transition: ".2s"
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = "#c82333";
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = "#dc3545";
                }}
              >
                <FiX size={20} />
              </button>
            </div>
            <div style={{
              padding: 24,
              overflowY: "auto",
              maxHeight: "60vh"
            }}>
              <pre style={{
                whiteSpace: "pre-wrap",
                fontSize: 13,
                fontFamily: "monospace",
                color: "#fff",
                margin: 0,
                lineHeight: 1.5
              }}>{exameVisualizando.laudo}</pre>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
