                />
              </div>
            </>
          )}
        </div>
        {/* Botão Visualizar Laudo - sempre abaixo */}
        <button
          style={{ 
            ...buttonStyle, 
            background: '#0eb8d0', 
            color: '#fff', 
            marginTop: 'clamp(8px, 2vw, 12px)', 
            minWidth: 'clamp(140px, 25vw, 160px)', 
            fontSize: 'clamp(12px, 2.5vw, 14px)', 
            padding: "clamp(6px, 2vw, 8px) clamp(12px, 3vw, 16px)" 
          }}
          onClick={handleVisualizar}
        >Visualizar Laudo</button>
      </div>
      {/* Exibição do laudo ao lado (lado único) ou embaixo (ambos) */}
      {laudoTexto && lado === "Ambos" && (
        <div style={{
          flex: 1,
          minWidth: "clamp(280px, 90vw, 320px)",
          maxWidth: 'min(1200px, 98vw)',
          marginTop: 'clamp(12px, 2vw, 16px)',
          background: "#fff",
          color: "#222",
          borderRadius: 'clamp(8px, 1.5vw, 12px)',
          padding: 'clamp(12px, 2.5vw, 20px)',
          boxShadow: "0 8px 32px #00e0ff33",
          fontFamily: "monospace",
          fontSize: 'clamp(11px, 2.2vw, 13px)',
          lineHeight: 1.5,
          whiteSpace: "pre-wrap",
          maxHeight: "75vh",
          overflowY: "auto"
        }}>
          <div style={{ 
            display: "flex", 
            justifyContent: "flex-end", 
            gap: 'clamp(6px, 1.5vw, 8px)', 
            marginBottom: 'clamp(6px, 1.5vw, 8px)' 
          }}>
            <button style={{ 
              ...buttonStyle, 
              background: "#0eb8d0", 
              color: "#fff", 
              fontSize: 'clamp(10px, 2vw, 12px)', 
              padding: "clamp(4px, 1.5vw, 6px) clamp(8px, 2vw, 12px)" 
            }} onClick={() => {
              const blob = new Blob([laudoTexto], { type: "text/plain;charset=utf-8" });
              saveAs(blob, `Laudo_${nome}_${data}.txt`);
            }}>Salvar TXT</button>
            <button style={{ 
              ...buttonStyle, 
              background: "#0eb8d0", 
              color: "#fff", 
              fontSize: 'clamp(10px, 2vw, 12px)', 
              padding: "clamp(4px, 1.5vw, 6px) clamp(8px, 2vw, 12px)" 
            }} onClick={() => {
              // Salvar PDF
              const doc = new jsPDF();
              const lines = laudoTexto.split("\n");
              let y = 15;
              doc.setFont("courier", "normal");
              lines.forEach(line => {
                if (line.startsWith("PACIENTE:") || line.startsWith("DOPPLER") || line.startsWith("Sistema") || line.startsWith("CONCLUSÃO")) {
                  doc.setFont(undefined, "bold");
                } else {
                  doc.setFont(undefined, "normal");
                }
                doc.text(line, 10, y);
                y += 8;
                if (y > 280) { doc.addPage(); y = 15; }
              });
              doc.save(`Laudo_${nome}_${data}.pdf`);
            }}>Salvar PDF</button>
          </div>
          {laudoTexto}
        </div>
      )}
      <style>{`
        @keyframes logoGlow {
          0% {
            filter: drop-shadow(0 10px 32px #00e0ff90);
          }
          100% {
            filter: drop-shadow(0 18px 48px #00e0ffc0);
          }
        }
      `}</style>
    </div>
  );
}

const inputStyle = {
  background: "#f7fbff",
  border: "1.5px solid #0eb8d0",
  borderRadius: "clamp(5px, 1vw, 7px)",
  padding: "clamp(6px, 1.5vw, 8px) clamp(8px, 2vw, 12px)",
  fontSize: "clamp(13px, 2.5vw, 15px)",
  color: "#222",
  outline: "none",
  fontFamily: "inherit",
  fontWeight: 500,
  width: "clamp(140px, 25vw, 180px)",
  minWidth: 0
};

const buttonStyle = {
  background: "#0eb8d0",
  color: "#fff",
  fontWeight: 600,
  fontSize: "clamp(14px, 2.5vw, 16px)",
  padding: "clamp(8px, 2vw, 10px) clamp(16px, 3vw, 22px)",
  border: "none",
  borderRadius: "clamp(6px, 1.5vw, 8px)",
  cursor: "pointer",
  letterSpacing: 0.5,
  boxShadow: "0 2px 10px #00e0ff30",
  transition: ".2s",
  marginLeft: "clamp(6px, 1.5vw, 8px)",
  minWidth: "clamp(120px, 20vw, 140px)"
};

export default MMIIVenoso;
