                      <option value="pérvia e competente">Pérvia e competente</option>
                      <option value="pérvia e insuficiente">Pérvia e insuficiente</option>
                      <option value="não compressível e sem fluxo (trombose)">Não compressível e sem fluxo (trombose)</option>
                    </select>
                  </div>
                ))}
              </div>

              {/* Veias Perfurantes */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Veias Perfurantes</h4>
                <div style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 10 }}>
                  <span>Status:</span>
                  <select
                    value={camposD.perfurante}
                    onChange={e => {
                      const novos = { ...camposD };
                      novos.perfurante = e.target.value;
                      atualizaCampos("Direito", novos);
                    }}
                    style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                  >
                    <option value="competentes">Competentes</option>
                    <option value="incompetentes">Incompetentes</option>
                  </select>
                </div>
                {camposD.perfurante === "incompetentes" && (
                  <div style={{ display: "flex", gap: 10 }}>
                    <input
                      type="text"
                      placeholder="Localização"
                      value={camposD.local_perfurante_valor}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.local_perfurante_valor = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    />
                    <input
                      type="text"
                      placeholder="Tipo"
                      value={camposD.local_perfurante_tipo}
                      onChange={e => {
                        const novos = { ...camposD };
                        novos.local_perfurante_tipo = e.target.value;
                        atualizaCampos("Direito", novos);
                      }}
                      style={{ padding: 5, borderRadius: 4, border: "none", flex: 1 }}
                    />
                  </div>
                )}
              </div>

              {/* Observações */}
              <div style={{ marginBottom: 30 }}>
                <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Observações</h4>
                <textarea
                  value={camposD.obs}
                  onChange={e => {
                    const novos = { ...camposD };
                    novos.obs = e.target.value;
                    atualizaCampos("Direito", novos);
                  }}
                  placeholder="Observações específicas..."
                  style={{ 
                    width: "100%", 
                    padding: 10, 
                    borderRadius: 6, 
                    border: "none", 
                    minHeight: 80,
                    resize: "vertical"
                  }}
                />
              </div>
            </div>
          )}

          {/* Botões de Ação */}
          <div style={{ display: "flex", gap: 15, justifyContent: "center", marginTop: 30 }}>
            <button
              onClick={gerarLaudo}
              style={{
                background: "#11b581",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                padding: "12px 24px",
                fontWeight: 600,
                fontSize: 16,
                cursor: "pointer"
              }}
            >
              Visualizar Laudo
            </button>
            {exameEmEdicao ? (
              <button
                onClick={salvarEdicao}
                style={{
                  background: "#0eb8d0",
                  color: "#fff",
                  border: "none",
                  borderRadius: 8,
                  padding: "12px 24px",
                  fontWeight: 600,
                  fontSize: 16,
                  cursor: "pointer"
                }}
              >
                Atualizar e Gerar PDF
              </button>
            ) : (
              <button
                onClick={handleSalvarExame}
                style={{
                  background: "#0eb8d0",
                  color: "#fff",
                  border: "none",
                  borderRadius: 8,
                  padding: "12px 24px",
                  fontWeight: 600,
                  fontSize: 16,
                  cursor: "pointer"
                }}
              >
                Salvar e Gerar PDF
              </button>
            )}
          </div>

          {/* Visualização do Laudo */}
          {laudo && (
            <div style={{ marginTop: 30, padding: 20, background: "#1a2332", borderRadius: 10 }}>
              <h4 style={{ color: "#0eb8d0", marginBottom: 15 }}>Prévia do Laudo:</h4>
              <div style={{ 
                background: "#fff", 
                color: "#000", 
                padding: 15, 
                borderRadius: 8, 
                maxHeight: 400, 
                overflowY: "auto",
                whiteSpace: "pre-wrap",
                fontFamily: "monospace",
                fontSize: 14
              }}>
                {laudoHTMLView()}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
